var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithDomainName', function () {

    this.timeout(5000);

    xdescribe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "",
            "html": cache.load("")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "",
                    "country": '',
                    "description": " "
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "",
            "html": cache.load("")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "",
            "html": cache.load("")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": ""
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "",
                    "html": cache.load("")
                }

                it('should be true when number of items and pages correct', function (done) {

                    Structure(payload, function (err, pagination) {
                        var expected = JSON.stringify({
                            "items": '',
                            "pages": ''
                        })
                        JSON.stringify(pagination).should.equal(expected);
                        done()
                    })
                });

            });


    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.accessorygeeks.com/geeky-kitchen.html",
            "html": cache.load("https://www.accessorygeeks.com/geeky-kitchen.html")
        }

        // Change to be the number of products on the page
        var x = 48;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Eutuxia Silicone Sink Water Splash Guard w/ Suction Cups for Kitchen, Bathroom. Protect Yourself From Splatter & Wet Floor. Universal for Standard Sinks. Perfect for Dishwashing. Heat Resistant.';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '6.99',
                    url: 'https://www.accessorygeeks.com/universal-silicone-kitchen-sink-splash-guard-w-suction-sink-guard.html',
                    image: 'https://1728bf4f7692c1b870b6-2bc710ea27c4d2aa194d96d0cade4c59.ssl.cf2.rackcdn.com/SINK-GUARD.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = '[JM Green] RRe [4 Cell Extra Large] Bento Box Freezer Tray. Flexible, Twistable Pop Push Method. BPA Free. Double Sealing Lid Provided. [Baby Pink]';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '8.88',
                    url: 'https://www.accessorygeeks.com/jmg-4cellftxlbpi-4-cell-extra-large-bento-box-freezer-tray-baby-pink.html',
                    image: 'https://1728bf4f7692c1b870b6-2bc710ea27c4d2aa194d96d0cade4c59.ssl.cf2.rackcdn.com/JMG-4CELLFTXLBPI.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});


