var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.activerideshop.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.activerideshop.com/collections/clothing-womens/products/rvca-in-a-haze-ls-woven-1000032184",
            "html": cache.load("https://www.activerideshop.com/collections/clothing-womens/products/rvca-in-a-haze-ls-woven-1000032184")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "RVCA In A Haze Ls Woven",
                    "price": "54.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://cdn.shopify.com/s/files/1/1786/2963/products/1000032184rvcainahazelswovenhenna1_2048x2048.jpg?v=1535043405",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.activerideshop.com/products/vans-classic-patch-trucker-youth-hat",
            "html": cache.load("https://www.activerideshop.com/products/vans-classic-patch-trucker-youth-hat")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Vans Classic Patch Trucker Youth Hat",
                    "price": "9.97",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://cdn.shopify.com/s/files/1/1786/2963/products/vansclassicpatchtruckerhatyouthnvywht1_2f328e7a-662c-450c-902f-00eea104ac9c_2048x2048.jpg?v=1529472187",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.activerideshop.com/collections/clothing-womens",
        "html": cache.load("https://www.activerideshop.com/collections/clothing-womens")
    }

    // Change to be the number of products on the page
    var x = 20;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Vans Cresent Mock Neck Crew Sweatshirt';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '49.99',
                url: 'https://www.activerideshop.com/collections/clothing-womens/products/vans-cresent-mock-neck-crew-sweatshirt-1000032178',
                image: 'https://cdn.shopify.com/s/files/1/1786/2963/products/1000032178vanscresentmockneckcrewsweatshirtcatawbagrape1.jpg?v=1535740873',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = "Levi's 721 Denim Rise Skinny";
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '59.99',
                url: 'https://www.activerideshop.com/collections/clothing-womens/products/levis-721-denim-rise-skinny-1000033422',
                image: 'https://cdn.shopify.com/s/files/1/1786/2963/products/1000033422levis721denimriseskinnybitesthedust1.jpg?v=1534751903',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.activerideshop.com/collections/clothing-womens",
            "html": cache.load("https://www.activerideshop.com/collections/clothing-womens")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Women's Clothing",
                    "url": payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});


