var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache.js')

describe('www.adorama.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.adorama.com/sanefav40b1.html",
            "html": cache.load("https://www.adorama.com/sanefav40b1.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Sanus Systems Euro Foundations EFAV40 AV Base & Shelves",
                    "price": "199.99",
                    "url": 'https://www.adorama.com/sanefav40b1.html',
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://www.adorama.com/images/Large/sanefav40b1.jpg',
                    "country": 'us',
                    "sku": 'sanefav40b1'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.adorama.com/RecommendFor/inkd3300kr.html"
        }

        it('should be false when product no longer available', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Nikon D3300 DSLR Camera w/18-55mm VR II Lens - Bla",
                    "price": null,
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://www.adorama.com/images/130x130/INKD3300KR.jpg',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Not a real search page', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.adorama.com/inkd5600bk2a.html"
        }

        it('should return null when the page is not a real search page', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.adorama.com/l/Home-Entertainment/Home-Theater/Home-Theater-Furniture",
            "html": cache.load("https://www.adorama.com/l/Home-Entertainment/Home-Theater/Home-Theater-Furniture")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 108,
                    "pages": 8,
                    "url": 'https://www.adorama.com/l/Home-Entertainment/Home-Theater/Home-Theater-Furniture'
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

     describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.adorama.com/l/Home-Entertainment/Home-Theater/Home-Theater-Furniture",
            "html": cache.load("https://www.adorama.com/l/Home-Entertainment/Home-Theater/Home-Theater-Furniture")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Home Theater Furniture",
                    "url": 'https://www.adorama.com/l/Home-Entertainment/Home-Theater/Home-Theater-Furniture'
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.adorama.com/l/Cameras/Nikon~Digital-SLR-Cameras",
            "html": cache.load("https://www.adorama.com/l/Cameras/Nikon~Digital-SLR-Cameras")
        }

        // Change to be the number of products on the page
        var x = 15;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Nikon D500 DSLR Body';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err ,result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '1896.95',
                    url: 'https://www.adorama.com/inkd500.html',
                    image: 'https://www.adorama.com/images/product/inkd500.jpg',
                    country: 'us',
                    sku: 'inkd500'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Nikon D5600 DSLR with DX 18-55mm f/3.5-5.6G VR & DX 70-300mm f/4.5-6.3G ED Lens';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '846.95',
                    url: 'https://www.adorama.com/inkd5600bk2.html',
                    image: 'https://www.adorama.com/images/product/inkd5600bk2.jpg',
                    country: 'us',
                    sku: 'inkd5600bk2'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
