var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.advancedmp3players.co.uk', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.advancedmp3players.co.uk/shop/MP3_Players.1/FiiO.406/FIIOM7/FiiO_M7_Hi-Res_Lossless_Audio_Player_with_Bluetooth.15232.html?amp3sid=7ck65cldhmipke3iq8alkkqk25",
            "html": cache.load("https://www.advancedmp3players.co.uk/shop/MP3_Players.1/FiiO.406/FIIOM7/FiiO_M7_Hi-Res_Lossless_Audio_Player_with_Bluetooth.15232.html?amp3sid=7ck65cldhmipke3iq8alkkqk25")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "FiiO M7 Hi-Res Lossless Audio Player with Bluetooth",
                    "price": "159.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.advancedmp3players.co.uk/shop/images/products/FIIOM7/FIIOM7small.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.advancedmp3players.co.uk/shop/MP3_Players.1/HiBy.681/HIBYR6-BLACK/HiBy_R6_Android_Audio_Player-Black.15264.html?amp3sid=7ck65cldhmipke3iq8alkkqk25",
            "html": cache.load("https://www.advancedmp3players.co.uk/shop/MP3_Players.1/HiBy.681/HIBYR6-BLACK/HiBy_R6_Android_Audio_Player-Black.15264.html?amp3sid=7ck65cldhmipke3iq8alkkqk25")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "HiBy R6 Android Audio Player-Black",
                    "price": "529.00",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://www.advancedmp3players.co.uk/shop/images/products/HIBYR6-BLACK/HIBYR6-BLACKSMALL.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.advancedmp3players.co.uk/shop/MP3_Players.1/?amp3sid=7ck65cldhmipke3iq8alkkqk25#guide_result",
        "html": cache.load("https://www.advancedmp3players.co.uk/shop/MP3_Players.1/?amp3sid=7ck65cldhmipke3iq8alkkqk25#guide_result")
    }

    // Change to be the number of products on the page
    var x = 24;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Cowon iAudio U7 32GB Digital Audio Player';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '59.99',
                url: 'https://www.advancedmp3players.co.uk/shop/+.0/Cowon.609/U732GB/Cowon_iAudio_U7_32GB_Digital_Audio_Player.15290.html?amp3sid=7ck65cldhmipke3iq8alkkqk25',
                image: 'https://www.advancedmp3players.co.uk/shop/images/listing_images/products/U732GB/U732GBSMALL.jpg',
                country: 'uk'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Astell & Kern A&ultima SP1000 Flagship Digital Audio Player';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '3299.00',
                url: 'https://www.advancedmp3players.co.uk/shop/MP3_Players.1/Astell___Kern.636/SP1000/Astell___Kern_A_ultima_SP1000_Flagship_Digital_Audio_Player.15112.html?amp3sid=7ck65cldhmipke3iq8alkkqk25',
                image: 'https://www.advancedmp3players.co.uk/shop/images/listing_images/products/SP1000/SP1000SMALL.jpg',
                country: 'uk'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.advancedmp3players.co.uk/shop/MP3_Players.1/?amp3sid=7ck65cldhmipke3iq8alkkqk25#guide_result",
            "html": cache.load("https://www.advancedmp3players.co.uk/shop/MP3_Players.1/?amp3sid=7ck65cldhmipke3iq8alkkqk25#guide_result")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "MP3 Players",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
