var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.aeropostale.com/nyc-at-night-graphic-t/product.jsp?productId=13159653&cp=3534618.3534619.3534623.3541050.3536151"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "NYC At Night Graphic T",
                    "price": "9.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://www.aeropostale.com/graphics/product_images/pAERO1-24781437t386x450.jpg',
                    "country": 'us',
                    "description": "You'll be over the moon about our NYC At Night Graphic T 'cause it boasts a cool combo of soft, lightweight fabric and retro style. It's decked out with a city silhouette and a night sky full of twinkling stars -- such a pretty sight!Slim fit. Approx. length (S): 25.5\"Style: 3310. Imported.60% cotton, 40% polyester.Machine wash/dry."
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    /*describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : ""
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });*/

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.aeropostale.com/girls-clothing/tops/family.jsp?categoryId=3536151"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 48,
                    "pages": 3
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.aeropostale.com/girls-clothing/tops/family.jsp?categoryId=3536151"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "TOPS"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.aeropostale.com/men-teen-boys/tops/t-shirts/"
        }

        // Change to be the number of products on the page
        var x = 165;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Aero Mountains Graphic Tee***';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '8.00',
                    url: 'http://www.aeropostale.com/aero-mountains-graphic-tee/60012329.html?dwvar_60012329_color=608&cgid=guys-tops-tees',
                    image: 'http://www.aeropostale.com/dw/image/v2/BBSG_PRD/on/demandware.static/-/Sites-master-catalog-aeropostale/default/dw500a3006/60012329_608_main.jpg?sw=460&sh=535&sm=fit&sfrm=jpg',
                    country: 'us',
                    sku: '60012329'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Aero 87 Original Stretch Graphic Tee';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '26.50',
                    url: 'http://www.aeropostale.com/aero-87-original-stretch-graphic-tee/60052200.html?dwvar_60052200_size=9100&dwvar_60052200_color=102&cgid=guys-tops-tees',
                    image: 'http://www.aeropostale.com/dw/image/v2/BBSG_PRD/on/demandware.static/-/Sites-master-catalog-aeropostale/default/dwbd5e8fe9/60052200_102_main.jpg?sw=460&sh=535&sm=fit&sfrm=jpg',
                    country: 'us',
                    sku: '60052200'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
