var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithDomainName', function () {

    this.timeout(5000);

    xdescribe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "",
            "html": cache.load("")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "",
                    "country": '',
                    "description": " "
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "",
            "html": cache.load("")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "",
            "html": cache.load("")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": ""
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "",
                    "html": cache.load("")
                }

                it('should be true when number of items and pages correct', function (done) {

                    Structure(payload, function (err, pagination) {
                        var expected = JSON.stringify({
                            "items": '',
                            "pages": ''
                        })
                        JSON.stringify(pagination).should.equal(expected);
                        done()
                    })
                });

            });


    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.ahalife.com/shop/sale",
            "html": cache.load("https://www.ahalife.com/shop/sale")
        }
        // Change to be the number of products on the page
        var x = 24;
        it('should return '+x+' products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        it('first product should be ', function (done) {
            Structure(payload, function(err, result) {
                var last = result.shift()
                last.name.should.be.equal('Soy-blend Candle')
                last.price.should.be.equal('41.65')
                last.url.should.be.equal('https://www.ahalife.com/product/149000064710/soy-blend-candle')
                last.image.should.be.equal('https://cdn2.ahalife.com/assets/compressed-images/119001379201/240wide.jpg')
                last.country.should.be.equal('us')
                should(last.barcode).be.equal(null)
                done()
            });
        })
        it('last product should be ', function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                last.name.should.be.equal('Lemon Summer Glasses (Set of 4)')
                last.price.should.be.equal('75.00')
                last.url.should.be.equal('https://www.ahalife.com/product/149000064620/lemon-summer-glasses-set-of-4')
                last.image.should.be.equal('https://cdn2.ahalife.com/assets/uncategorized-new/119001368434/240wide.jpg')
                last.country.should.be.equal('us')
                should(last.barcode).be.equal(null)
                done()
            });
        })


    });

});


