var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.alexandalexa.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.alexandalexa.com/en/product/225645/petit-by-sofie-schnoor-sweat-nyc-blue",
            "html": cache.load("https://www.alexandalexa.com/en/product/225645/petit-by-sofie-schnoor-sweat-nyc-blue")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Sweat Nyc Blue",
                    "price": "38.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.alexandalexa.com/images/437474/large.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.alexandalexa.com/en/product/35619/peach-angel-winged-giraffe-tee",
            "html": cache.load("https://www.alexandalexa.com/en/product/35619/peach-angel-winged-giraffe-tee")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Peach Angel Winged Giraffe Tee",
                    "price": null,
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://www.alexandalexa.com/images/35136/large.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.alexandalexa.com/en/1/babies",
        "html": cache.load("https://www.alexandalexa.com/en/1/babies")
    }

    // Change to be the number of products on the page
    var x = 36;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Knitted Cardigan Monkey Grey Melange';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '46.00',
                url: 'https://www.alexandalexa.com/en/product/234724/one-we-like-knitted-cardigan-monkey-grey-melange',
                image: 'https://www.alexandalexa.com/images/437695/medium.jpg',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Body L. Purple';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '43.00',
                url: 'https://www.alexandalexa.com/en/product/225637/petit-by-sofie-schnoor-body-l-purple',
                image: 'https://www.alexandalexa.com/images/437475/medium.jpg',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.alexandalexa.com/en/1/babies",
            "html": cache.load("https://www.alexandalexa.com/en/1/babies")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Babies",
                    "url": payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});


