var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.algenist.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.algenist.com/collections/makeup/products/reveal-6-in-1-color-correcting-concealer",
            "html": cache.load("https://www.algenist.com/collections/makeup/products/reveal-6-in-1-color-correcting-concealer")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "REVEAL 6-in-1 Color Correcting Concealer",
                    "price": "28.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://cdn.shopify.com/s/files/1/2461/2327/products/abcdfa30a3000d5460a71ca7e1c41b9e_600x.png?v=1519334487",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.algenist.com/collections/discoloration/products/overnight-restorative-cream",
            "html": cache.load("https://www.algenist.com/collections/discoloration/products/overnight-restorative-cream")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Overnight Restorative Cream",
                    "price": "94.00",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://cdn.shopify.com/s/files/1/2461/2327/products/8bcb1df9368985ed5b1d5e805b4ab9ea_600x.png?v=1519324937",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.algenist.com/collections/makeup",
        "html": cache.load("https://www.algenist.com/collections/makeup")
    }

    // Change to be the number of products on the page
    var x = 24;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'REVEAL Color Correcting Anti-Aging Serum Foundation SPF 15';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '38.00',
                url: 'https://www.algenist.com/collections/makeup/products/reveal-color-correcting-anti-aging-serum-foundation-spf-15',
                image: 'https://cdn.shopify.com/s/files/1/2461/2327/products/b04137405e80bf03f7cf6d3581b57d24_300x300.png?v=1512711310',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Bronzing Powder Brush';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '35.00',
                url: 'https://www.algenist.com/collections/makeup/products/bronzing-powder-brush',
                image: 'https://cdn.shopify.com/s/files/1/2461/2327/products/e20850d898497074a34915335888e209_300x300.png?v=1512711471',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.algenist.com/collections/makeup",
            "html": cache.load("https://www.algenist.com/collections/makeup")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Makeup",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
