var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithDomainName', function () {

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "http://www.all-battery.com/Tenergy-AirPurifier-58047.aspx",
            "html": cache.load("http://www.all-battery.com/Tenergy-AirPurifier-58047.aspx")
        }

        it('should be true when product is in stock', function () {
            
            const inStockPage =  Structure(payload)  
            console.log(inStockPage)          
            inStockPage.name.should.be.equal("Tenergy TAP01 Air Purifier with True HEPA Filter");
            inStockPage.price.should.be.equal("109.99");
            inStockPage.url.should.be.equal("http://www.all-battery.com/Tenergy-AirPurifier-58047.aspx");
            inStockPage.inStock.should.be.equal(true);
            should(inStockPage.stockLevel).be.equal(null);
            inStockPage.image.should.be.equal('http://www.all-battery.com/images/products/display/58047_01.jpg');
            inStockPage.country.should.be.equal('us');            
         });
 
 
    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "http://www.all-battery.com/Tenergy-OtisAccessory-58052.aspx",
            "html": cache.load("http://www.all-battery.com/Tenergy-OtisAccessory-58052.aspx")
        }

        
        it('should be true when product is in stock', function () {
            
            const inStockPage =  Structure(payload)         
            console.log(inStockPage)   
            inStockPage.name.should.be.equal("HEPA Filter and Side Brush Replacement Kit for Tenergy TRV01 Robot Vacuum Cleaner");
            inStockPage.price.should.be.equal(null);
            inStockPage.url.should.be.equal("http://www.all-battery.com/Tenergy-OtisAccessory-58052.aspx");
            inStockPage.inStock.should.be.equal(false);
            should(inStockPage.stockLevel).be.equal(null);
            inStockPage.image.should.be.equal('http://www.all-battery.com/images/products/display/58052.jpg');
            inStockPage.country.should.be.equal('us');            
         });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.all-battery.com/home-appliances.aspx",
            "html": cache.load("http://www.all-battery.com/home-appliances.aspx")
        }
        it('should be true when number of items and pages correct', function () {

            var expected = JSON.stringify({
                "name": "Home Appliances",
                "url":"http://www.all-battery.com/home-appliances.aspx"
            })

            const pagination = Structure(payload)

            JSON.stringify(pagination).should.equal(expected);
        });

    });

    describe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "http://www.all-battery.com/home-appliances.aspx",
                    "html": cache.load("http://www.all-battery.com/home-appliances.aspx")
                }

                it('should be true when number of items and pages correct', function () {

                    var expected = JSON.stringify({
                        "items": "26",
                        "pages":"",
                        "url":"http://www.all-battery.com/home-appliances.aspx"
                    })
        
                    const category = Structure(payload)
        
                    JSON.stringify(category).should.equal(expected);
                });        

            });


    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "http://www.all-battery.com/home-appliances.aspx",
            "html": cache.load("http://www.all-battery.com/home-appliances.aspx")
        }

        // Change to be the number of products on the page
        var x = 26;
        it('should return ' + x + ' numer of products', function () {
            let result = Structure(payload)
            result.length.should.equal(x);
        });

        var firstProductName = 'HEPA Filter and Side Brush Replacement Kit for Tenergy TRV01 Robot Vacuum Cleaner';
        it('first product should be ' + firstProductName, function() {

            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: null,
                url: 'http://www.all-battery.com/Tenergy-OtisAccessory-58052.aspx',
                image: 'http://www.all-battery.com/images/products/thumb/58052.jpg',
                country: 'us'
            })
            
            let result = Structure(payload)

            var first = result.shift()
            
            JSON.stringify(first).should.equal(expected)

        })

        var lastProductName = 'Tenergy Tempered Glass Digital Kitchen Scale with Tare Feature, 11lb/5kg Capacity';
        it('last product should be ' + lastProductName, function () {
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '11.99',
                url: 'http://www.all-battery.com/Tenergy-Kitchenscale-58010.aspx',
                image: 'http://www.all-battery.com/images/products/thumb/58010_01.2.jpg',
                country: 'us'
            })

            const result = Structure(payload);

            var last = result.pop()
            JSON.stringify(last).should.equal(expected)
        })

    }); 


});


