describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.allcosmeticswholesale.com/shop/eyes/eye-shadow.html",
        "html": cache.load("https://www.allcosmeticswholesale.com/shop/eyes/eye-shadow.html")
    }

    // Change to be the number of products on the page
    var x = 24;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'COVERGIRL Bombshell Shineshadow - Platinum Club...';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '3.49',
                url: 'https://www.allcosmeticswholesale.com/shop/eyes/eye-shadow/covergirl-bombshell-shineshadow-platinum-club-310.html',
                image: 'https://pull01-smbsales.netdna-ssl.com/media/catalog/product/cache/2/small_image/175x175/9df78eab33525d08d6e5fb8d27136e95/c/o/covergirl-bombshell-shineshadow-platinum-club-buy-now-acw.jpg',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'NARS Velvet Shadow Stick - Sukhothai u/b';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '16.99',
                url: 'https://www.allcosmeticswholesale.com/shop/eyes/eye-shadow/nars-velvet-shadow-stick-sukhothai-u-b.html',
                image: 'https://pull01-smbsales.netdna-ssl.com/media/catalog/product/cache/2/small_image/175x175/9df78eab33525d08d6e5fb8d27136e95/n/a/nars_velvet_shadow_stick_sukhothai_acw.jpg',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.allcosmeticswholesale.com/shop/eyes/eye-shadow.html",
            "html": cache.load("https://www.allcosmeticswholesale.com/shop/eyes/eye-shadow.html")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Eye Shadow",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
