var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.allenedmonds.com', function () {

    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.allenedmonds.com/clothing/mens-clothing/outerwear/moorland-suede-field-jacket/SF1014585.html?dwvar_SF1014585_color=1014585",
            "html": cache.load("https://www.allenedmonds.com/clothing/mens-clothing/outerwear/moorland-suede-field-jacket/SF1014585.html?dwvar_SF1014585_color=1014585")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Moorland Suede Field Jacket",
                    "price": "1195.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.allenedmonds.com/dw/image/v2/AAXI_PRD/on/demandware.static/-/Sites-allenedmonds-catalog/default/dw78ce1197/images/2.1/accessories/jackets/coat-1014585-astonleather-suedefield-cocoa-front-web.jpg?sw=736&sh=736&sm=fit",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.allenedmonds.com/stripe-tie/1587BRN-STD-STD.html",
            "html": cache.load("https://www.allenedmonds.com/stripe-tie/1587BRN-STD-STD.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Stripe Tie",
                    "price": "19.97",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://www.allenedmonds.com/dw/image/v2/AAXI_PRD/on/demandware.static/-/Sites-allenedmonds-catalog/default/dwc4e641c7/images/2.1/0/accessories/1587brntie2016.jpg?sw=736&sh=736&sm=fit",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.allenedmonds.com/clothing/mens-clothing/",
        "html": cache.load("https://www.allenedmonds.com/clothing/mens-clothing/")
    }

    // Change to be the number of products on the page
    var x = 26;
    it('should return ' + x + ' numer of products', function () {
        let result = Structure(payload)
        result.length.should.equal(x);
    });

    var firstProductName = 'HEPA Filter and Side Brush Replacement Kit for Tenergy TRV01 Robot Vacuum Cleaner';
    it('first product should be ' + firstProductName, function() {

        var expected = JSON.stringify({
            barcode: null,
            name: firstProductName,
            price: null,
            url: 'http://www.all-battery.com/Tenergy-OtisAccessory-58052.aspx',
            image: 'http://www.all-battery.com/images/products/thumb/58052.jpg',
            country: 'us'
        })
        
        let result = Structure(payload)

        var first = result.shift()
        
        JSON.stringify(first).should.equal(expected)

    })

    var lastProductName = 'Tenergy Tempered Glass Digital Kitchen Scale with Tare Feature, 11lb/5kg Capacity';
    it('last product should be ' + lastProductName, function () {
        var expected = JSON.stringify({
            barcode: null,
            name: lastProductName,
            price: '11.99',
            url: 'http://www.all-battery.com/Tenergy-Kitchenscale-58010.aspx',
            image: 'http://www.all-battery.com/images/products/thumb/58010_01.2.jpg',
            country: 'us'
        })

        const result = Structure(payload);

        var last = result.pop()
        JSON.stringify(last).should.equal(expected)
    })



    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.allenedmonds.com/clothing/mens-clothing/",
            "html": cache.load("https://www.allenedmonds.com/clothing/mens-clothing/")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Men's Clothing",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
