    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.allmodern.com/decor-pillows/sb0/wall-clocks-c429917.html",
            "html": cache.load("https://www.allmodern.com/decor-pillows/sb0/wall-clocks-c429917.html")
        }

        // Change to be the number of products on the page
        var x = 48;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = '15" Wall Clock';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '37.99',
                    url: 'https://www.allmodern.com/decor-pillows/pdp/15-wall-clock-clrb1731.html',
                    image: 'https://secure.img1-fg.wfcdn.com/im/94062711/resize-h310-w310%5Ecompr-r85/4434/44340764/15-wall-clock.jpg',
                    country: 'uk',
                    sku: 'clrb1731'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Oversized Round Gold 48" Wall Clock';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '164.99',
                    url: 'https://www.allmodern.com/decor-pillows/pdp/oversized-round-gold-48-wall-clock-dbyh8103.html',
                    image: 'https://secure.img1-fg.wfcdn.com/im/71310300/resize-h310-w310%5Ecompr-r85/3905/39058998/oversized-round-gold-48-wall-clock.jpg',
                    country: 'uk',
                    sku: "dbyh8103",
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    })

