var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.alloutdoor.co.uk', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.alloutdoor.co.uk/clothing/jackets-waterproofs/berghaus-womens-dalemaster-shell-jacket-af-.htm",
            "html": cache.load("https://www.alloutdoor.co.uk/clothing/jackets-waterproofs/berghaus-womens-dalemaster-shell-jacket-af-.htm")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Berghaus Womens Dalemaster Shell Jacket",
                    "price": "75.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://alloutdoor.2dimg.com/4/1507883730_2268_b0151e9b65.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.alloutdoor.co.uk/bags-packs/stuff-sacks-packing-cubes/trekmates-mesh-stuff-bag-black-xlarge-.htm",
            "html": cache.load("https://www.alloutdoor.co.uk/bags-packs/stuff-sacks-packing-cubes/trekmates-mesh-stuff-bag-black-xlarge-.htm")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Trekmates Mesh Stuff Bag Black (XLarge)",
                    "price": "5.95",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://alloutdoor.2dimg.com/4/1338553283_83.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.alloutdoor.co.uk/clothing/jackets-waterproofs/",
        "html": cache.load("https://www.alloutdoor.co.uk/clothing/jackets-waterproofs/")
    }

    // Change to be the number of products on the page
    var x = 60;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Berghaus Mens Dalemaster Shell Jacket';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '75.99',
                url: 'https://www.alloutdoor.co.uk/clothing/jackets-waterproofs/berghaus-mens-dalemaster-shell-jacket-am-.htm',
                image: 'https://alloutdoor.2dimg.com/3/1507879221_4033_9cba9d477c.jpg',
                country: 'uk'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Jack Wolfskin Womens Turbulence Jacket';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '50.35',
                url: 'https://www.alloutdoor.co.uk/clothing/jackets-waterproofs/jack-wolfskin-womens-turbulence-jacket.htm',
                image: 'https://alloutdoor.2dimg.com/3/1519656282_9772.jpg',
                country: 'uk'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.alloutdoor.co.uk/clothing/jackets-waterproofs/",
            "html": cache.load("https://www.alloutdoor.co.uk/clothing/jackets-waterproofs/")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Jackets & Waterproofs",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
