describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.allsaints.com/women/coats-and-jackets/style,any/colour,any/size,any/",
        "html": cache.load("https://www.allsaints.com/women/coats-and-jackets/style,any/colour,any/size,any/")
    }

    // Change to be the number of products on the page
    var x = 45;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Leni Stud Coat';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '318.00',
                url: 'https://www.allsaints.com/women/coats-and-jackets/allsaints-leni-stud-coat/?colour=5&category=840',
                image: 'https://images.allsaints.com/products/600/WO020P/5/WO020P-5-1.jpg',
                country: 'uk'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Hay Denim Buckle Jacket';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '108.00',
                url: 'https://www.allsaints.com/women/coats-and-jackets/allsaints-hay-denim-buckle-jac/?colour=2824&category=840',
                image: 'https://images.allsaints.com/products/600/WO007N/2824/WO007N-2824-1.jpg',
                country: 'uk'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.allsaints.com/women/coats-and-jackets/style,any/colour,any/size,any/",
            "html": cache.load("https://www.allsaints.com/women/coats-and-jackets/style,any/colour,any/size,any/")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Women's Coats & Jackets",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
