var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.allstarhealth.com', function () {

    this.timeout(10000);

    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.allstarhealth.com/f/bodystrong-100_veggie_protein.htm",
            "html": cache.load("https://www.allstarhealth.com/f/bodystrong-100_veggie_protein.htm")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "BodyStrong 100% Veggie Protein Natural Chocolate 5 lbs",
                    "price": "41.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://dnm8jy24zqiqb.cloudfront.net/prodpics/250x250/161104-P37968.JPG",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.allstarhealth.com/f/frs-liquid_concentrate.htm",
            "html": cache.load("https://www.allstarhealth.com/f/frs-liquid_concentrate.htm")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "FRS Healthy Energy Liquid Concentrate Peach Mango 32 fl.oz",
                    "price": "15.79",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://dnm8jy24zqiqb.cloudfront.net/prodpics/250x250/100124-P11083.JPG",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.allstarhealth.com/lj_c/protein.htm?sort=relevance",
        "html": cache.load("https://www.allstarhealth.com/lj_c/protein.htm?sort=relevance")
    }

    // Change to be the number of products on the page
    var x = 20;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'BodyStrong100% Casein Protein';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: null,
                url: 'https://www.allstarhealth.com/f/bodystrong-100percent_casein_protein.htm',
                image: 'https://dnm8jy24zqiqb.cloudfront.net/prodpics/100x100/171017-P27164.JPG',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Boundless NutritionOatmega Grass Fed Whey Protein Bar';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '16.99',
                url: 'https://www.allstarhealth.com/f/boundless_nutrition-oatmega_grass_fed_whey.htm',
                image: 'https://dnm8jy24zqiqb.cloudfront.net/prodpics/100x100/171116-P39324.JPG',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.allstarhealth.com/lj_c/protein.htm?sort=relevance",
            "html": cache.load("https://www.allstarhealth.com/lj_c/protein.htm?sort=relevance")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Protein",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});


