var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.amara.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.amara.com/products/podium-storage-jar-set-with-stand-grey-5-piece",
            "html": cache.load("https://www.amara.com/products/podium-storage-jar-set-with-stand-grey-5-piece")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Podium Storage Jar Set with Stand - Grey - 5 Piece",
                    "price": "50.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.amara.com/static/uploads/images-2/products/x/huge/858362/podium-storage-jar-set-with-stand-grey-5-piece-615307.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.amara.com/ie/products/enamel-side-table-set-of-2-green-grey",
            "html": cache.load("https://www.amara.com/ie/products/enamel-side-table-set-of-2-green-grey")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Enamel Side Table - Set of 2 - Green/Grey",
                    "price": "528",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://www.amara.com/static/uploads/images-2/products/x/huge/853189/enamel-side-table-set-of-2-green-grey-955856.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.amara.com/shop/jars-canisters",
        "html": cache.load("https://www.amara.com/shop/jars-canisters")
    }

    // Change to be the number of products on the page
    var x = 48;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Pumpkin Jar';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '45',
                url: 'https://www.amara.com/products/pumpkin-jar',
                image: 'https://www.amara.com/static/uploads/images-2/products/main/144686/pumpkin-jar-968610.jpg',
                country: 'uk'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Trattoria Storage Jar - Tobacco';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '22',
                url: 'https://www.amara.com/products/trattoria-storage-jar-tobacco',
                image: 'https://www.amara.com/static/uploads/images-0/products/trattoria-storage-jar-tobacco-1l-jpg.jpg',
                country: 'uk'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.amara.com/shop/jars-canisters",
            "html": cache.load("https://www.amara.com/shop/jars-canisters")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Jars & Canisters",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
