var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')


describe('replaceWithwebAddress', function () {

    this.timeout(10000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "http://www.americangolf.co.uk/gps-bags-equipment/trolley-cart-bags/motocaddy-2016-dry-series-cart-bag-295600.html?dwvar_295600_variantimage=black%20red",
            "html": cache.load("http://www.americangolf.co.uk/gps-bags-equipment/trolley-cart-bags/motocaddy-2016-dry-series-cart-bag-295600.html?dwvar_295600_variantimage=black%20red")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Motocaddy 2016 Dry-Series Cart Bag",
                    "price": "179.99",
                    "url": payload.url,
                    "inStock": "UNCHECKED",
                    "stockLevel": null,
                    "image": 'http://demandware.edgesuite.net/sits_pod37/dw/image/v2/AAKY_PRD/on/demandware.static/-/Sites-master-catalog/default/dwdead4929/zoom/295600_whtred.jpg?sw=340&sh=227',
                    "country": 'uk',
                    "sku": '295600'

                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page pagination', function () {

        var payload = {
            "pageType": "pagination",
            "url": "http://www.americangolf.co.uk/gps-bags-equipment/all-golf-bags",
            "html": cache.load("http://www.americangolf.co.uk/gps-bags-equipment/all-golf-bags")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function (err, pagination) {
                var expected = JSON.stringify({
                    "items": 12,
                    "pages": 17,
                    "url": "http://www.americangolf.co.uk/gps-bags-equipment/all-golf-bags"

                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Not a Search page', function () {

        var payload = {
            "pageType": "pagination",
            "url": "http://www.americangolf.co.uk/gps-bags-equipment/trolley-cart-bags/titleist-deluxe-cart-bag-294307.html?dwvar_294307_variantimage=black%2Fgrey%2Forange",
            "html": cache.load("https://www.screwfix.com/c/electrical-lighting/switches-sockets/cat830530#category=cat830530&page_size=20&page_start=20")
        }

        it('should be true when pages and items return null', function (done) {

            Structure(payload, function (err, pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null,
                    "url": "http://www.americangolf.co.uk/gps-bags-equipment/trolley-cart-bags/titleist-deluxe-cart-bag-294307.html?dwvar_294307_variantimage=black%2Fgrey%2Forange"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.americangolf.co.uk/gps-bags-equipment/all-golf-gps?sz=12&start=24",
            "html": cache.load("https://www.americangolf.co.uk/gps-bags-equipment/all-golf-gps?sz=12&start=24")
        }

        // Change to be the number of products on the page
        var x = 32;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'TomTom Golfer Limited Edition GPS Watch';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '119.99',
                    url: 'https://www.americangolf.co.uk/gps-bags-equipment/gps-watches/tomtom-golfer-limited-edition-gps-watch-304673.html?dwvar_304673_variantimage=black%2Fgreen',
                    image: 'https://www.americangolf.co.uk/dw/image/v2/AAKY_PRD/on/demandware.static/-/Sites-master-catalog/default/dw174929a5/zoom/304673-1.jpg?sw=255',
                    country: 'uk',
                    sku: "304673"
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Garmin S60 QuickFit Leather Watch Strap';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '69.99',
                    url: 'https://www.americangolf.co.uk/gps-bags-equipment/gps-watches/garmin-s60-quickfit-leather-watch-strap-318095.html?dwvar_318095_variantimage=brown',
                    image: 'https://www.americangolf.co.uk/dw/image/v2/AAKY_PRD/on/demandware.static/-/Sites-master-catalog/default/dwa68d733a/zoom/318095-brownleather.jpg?sw=255',
                    country: 'uk',
                    sku: "318095"
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});

describe('Search page category', function () {

    var payload = {
        "pageType": "category",
        "url": "http://www.americangolf.co.uk/gps-bags-equipment/all-golf-bags",
        "html": cache.load("http://www.americangolf.co.uk/gps-bags-equipment/all-golf-bags")
    }

    it('should be true when number of items and pages correct', function (done) {

        Structure(payload, function (err, category) {
            var expected = JSON.stringify({
                "name": "All Golf bags",
                "url": "http://www.americangolf.co.uk/gps-bags-equipment/all-golf-bags"
            })
            JSON.stringify(category).should.equal(expected);
            done()
        })
    });

});

