var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.amerimark.com', function () {

    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.amerimark.com/totes-quilted-coat/057406.html",
            "html": cache.load("https://www.amerimark.com/totes-quilted-coat/057406.html")
        }

        it('should be true when product is in stock', function () {
            
            const inStockPage =  Structure(payload)            
            inStockPage.name.should.be.equal("Black Diamond HeavyWeight Waterproof Gloves");
            inStockPage.price.should.be.equal("14.99");
            inStockPage.url.should.be.equal("https://www.bitsandpieces.com/product/money-maze-bank");
            inStockPage.inStock.should.be.equal(true);
            should(inStockPage.stockLevel).be.equal(null);
            inStockPage.inStock.should.be.equal(true);
            inStockPage.image.should.be.equal('https://s1.thcdn.com/productimg/1600/1600/11580543-1104537722369471.jpg');
            inStockPage.country.should.be.equal('us');            
         });
 
    });

    xdescribe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.amerimark.com/strivectin-multi-action-cream/019728.html",
            "html": cache.load("https://www.amerimark.com/strivectin-multi-action-cream/019728.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "StriVectinÂ® Multi-Action Cream",
                    "price": "49.99",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://www.amerimark.com/images/01/019728/full/019728001.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.amerimark.com/1/jackets-outerwear-lincts/shop.html",
        "html": cache.load("https://www.amerimark.com/1/jackets-outerwear-lincts/shop.html")
    }

    // Change to be the number of products on the page
    var x = 7;
    it('should return ' + x + ' numer of products', function () {
        let result = Structure(payload)
        result.length.should.equal(x);
    });

    var firstProductName = 'Critters Go For A Ride 100 Large Piece Jigsaw Puzzle';
    it('first product should be ' + firstProductName, function() {

        var expected = JSON.stringify({
            barcode: null,
            name: firstProductName,
            price: '4.99',
            url: 'http://www.bitsandpieces.com/product/critters-go-for-a-ride-100-large-piece-jigsaw-puzzle-42777',
            image: 'https://s3.amazonaws.com/cdn.bitsandpieces.com/images/160/42777.jpg',
            country: 'us'
        })
        
        let result = Structure(payload)

        var first = result.shift()
        
        JSON.stringify(first).should.equal(expected)

    })

    var lastProductName = 'Soccer Sliding Wooden Puzzle';
    it('last product should be ' + lastProductName, function () {
        var expected = JSON.stringify({
            barcode: null,
            name: lastProductName,
            price: '14.99',
            url: 'http://www.bitsandpieces.com/product/soccer-sliding-wooden-puzzle',
            image: 'https://s3.amazonaws.com/cdn.bitsandpieces.com/images/160/43952.jpg',
            country: 'us'
        })

        const result = Structure(payload);

        var last = result.pop()
        JSON.stringify(last).should.equal(expected)
    })

})

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.amerimark.com/1/outerwear-linjck/shop.html",
            "html": cache.load("https://www.amerimark.com/1/outerwear-linjck/shop.html")
        }

        it('should be true when number of items and pages correct', function () {

            var expected = JSON.stringify({
                "name": "Children's Puzzles",
                "url":"https://www.bitsandpieces.com/category/childrens-puzzles"
            })

            const category = Structure(payload)

            JSON.stringify(category).should.equal(expected);
        });


    });