var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.amsterdamprinting.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.amsterdamprinting.com/p/under-armour-ladies-embroidered-qualifier--zip?id=46034",
            "html": cache.load("https://www.amsterdamprinting.com/p/under-armour-ladies-embroidered-qualifier--zip?id=46034")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Under Armour® Ladies Embroidered Qualifier ¼ Zip",
                    "price": "54.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://images.promotionalstore.com/is/image/Amsterdam/product_46034?id=F73rT1&fmt=jpg&fit=constrain,1&wid=293&hei=426&$ProductDetail_F$",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.amsterdamprinting.com/p/2018-attendance-calendar-form-25-pk?id=40161-0",
            "html": cache.load("https://www.amsterdamprinting.com/p/2018-attendance-calendar-form-25-pk?id=40161-0")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "2018 Attendance Calendar Form 25/Pk",
                    "price": "0.73",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://images.promotionalstore.com/is/image/Amsterdam/product_40161-0?id=lBoqd1&fmt=jpg&fit=constrain,1&wid=345&hei=336&$ProductDetail_F$",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.amsterdamprinting.com/c/custom-jackets?id=2352",
        "html": cache.load("https://www.amsterdamprinting.com/c/custom-jackets?id=2352")
    }

    // Change to be the number of products on the page
    var x = 40;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Team 365 Mens Leader Soft Shell Jacket';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '45.18',
                url: 'https://www.amsterdamprinting.com/p/team-365-mens-leader-soft-shell-jacket?id=45742',
                image: 'https://images.promotionalstore.com/is/image/PromotionalStore/i?src=is{PromotionalStore/product_45742}&$Category_T4$',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Embroidered Mens Brushed Microfleece Zip Jacket';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '43.00',
                url: 'https://www.amsterdamprinting.com/p/embroidered-mens-brushed-microfleece-zip-jacket?id=43564',
                image: 'https://images.promotionalstore.com/is/image/PromotionalStore/i?src=is{PromotionalStore/product_43564}&$Category_T4$',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.amsterdamprinting.com/c/custom-jackets?id=2352",
            "html": cache.load("https://www.amsterdamprinting.com/c/custom-jackets?id=2352")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Custom Outerwear",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
