describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.appliancecity.co.uk/sinks-taps/sinks/",
        "html": cache.load("https://www.appliancecity.co.uk/sinks-taps/sinks/")
    }

    // Change to be the number of products on the page
    var x = 12;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Franke KUBUS KBX160 34-16 LHSB Kubus 1.5 Bowl Sink Left Hand Small Bowl - SILKSTEELFranke KUBUS KBX160 34-16 LHSB Kubus 1.5 Bowl Sink Left Hand Small Bowl - SILKSTEEL';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '359.00',
                url: 'https://www.appliancecity.co.uk/sinks-taps/sinks/franke-kubus-kbx160-34-16-lhsb-kubus-1-5-bowl-sink-left-hand-small-bowl/',
                image: 'https://s3-eu-west-2.amazonaws.com/acitytesting/uploads/2017/09/Franke-kubus-kbx160-34-16l-300x300.jpg',
                country: 'uk'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Franke PEAK PKX110 34 Peak Single Bowl Undermount Sink - SILKSTEELFranke PEAK PKX110 34 Peak Single Bowl Undermount Sink - SILKSTEEL';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '309.00',
                url: 'https://www.appliancecity.co.uk/sinks-taps/sinks/franke-peak-pkx110-34-peak-single-bowl-undermount-sink/',
                image: 'https://s3-eu-west-2.amazonaws.com/acitytesting/uploads/2017/09/Franke-peak-pkx110-34-300x300.jpg',
                country: 'uk'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.appliancecity.co.uk/sinks-taps/sinks/",
            "html": cache.load("https://www.appliancecity.co.uk/sinks-taps/sinks/")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Sinks",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
