var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.appliancepartspros.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.appliancepartspros.com/ge-dishwasher-door-gasket-wd08x10057-ap3798079.html",
            "html": cache.load("https://www.appliancepartspros.com/ge-dishwasher-door-gasket-wd08x10057-ap3798079.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Dishwasher Door Gasket",
                    "price": "58.06",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://cdn.appliancepartspros.com/images/product/cache/ge-dishwasher-door-gasket-wd08x10057-ap3798079_01_m.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.appliancepartspros.com/bosch-dispenser-tray-00798752-ap5951086.html",
            "html": cache.load("https://www.appliancepartspros.com/bosch-dispenser-tray-00798752-ap5951086.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Dispenser Tray",
                    "price": "78.39",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://cdn.appliancepartspros.com/images/diagrams/dcache/30761986_4.gif",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.appliancepartspros.com/dishwasher-parts.html",
        "html": cache.load("https://www.appliancepartspros.com/dishwasher-parts.html")
    }

    // Change to be the number of products on the page
    var x = 24;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Silverware Basket';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '16.34',
                url: 'https://www.appliancepartspros.com/ge-dishwasher-silverware-basket-wd28x10128-ap3772889.html',
                image: 'https://cdn.appliancepartspros.com/images/product/cache/ge-dishwasher-silverware-basket-wd28x10128-ap3772889_01_th.jpg',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Water Inlet Valve';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '19.36',
                url: 'https://www.appliancepartspros.com/ge-valve-water-inlet-wd15x10014-ap5632416.html',
                image: 'https://cdn.appliancepartspros.com/images/product/cache/ge-valve-water-inlet-wd15x10014-ap5632416_01_th.jpg',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.appliancepartspros.com/dishwasher-parts.html",
            "html": cache.load("https://www.appliancepartspros.com/dishwasher-parts.html")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Dishwasher Parts",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});


