var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.areyougame.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.areyougame.com/interact/item.asp?itemno=ZM1260&sa=0",
            "html": cache.load("http://www.areyougame.com/interact/item.asp?itemno=ZM1260&sa=0")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Lords of Scotland",
                    "price": "19.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://www.areyougame.com/images/items/ZM1260.jpg',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product", 
            "url" : "http://www.areyougame.com/Interact/item.asp?itemno=JAX8080", 
            "html": cache.load("http://www.areyougame.com/Interact/item.asp?itemno=JAX8080") 
        } 

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Jumbo Sequence Game", 
                    "price": "53.98", 
                    "url": payload.url, 
                    "inStock": false, 
                    "stockLevel": null,
                    "image": 'http://www.areyougame.com/images/items/JAX8080.jpg',
                    "country": 'us' 
                }) 
                JSON.stringify(inStockPage).should.equal(expected); 
                done() 
            }) 
        }); 
    }); 

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search", 
            "url" : "http://www.areyougame.com/interact/category.asp?cat=Strategy+Games", 
            "html": cache.load("http://www.areyougame.com/interact/category.asp?cat=Strategy+Games") 
        }

        // Change to be the number of products on the page
        var x = 18;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Haleakala';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '34.99',
                    url: 'http://www.areyougame.com/interact/item.asp?itemno=ZM7630&sa=0',
                    image: 'http://www.areyougame.com/images/small/smZM7630.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Leather Chess, Checkers, & Backgammon - Deluxe 3-Game Set';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '29.98',
                    url: 'http://www.areyougame.com/interact/item.asp?itemno=CR6039930&sa=0',
                    image: 'http://www.areyougame.com/images/small/smCR6039930.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
