var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.ashford.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.ashford.com/us/watches/balmain/eria/eria-bijou/B17713282.pid?nid=cpg_cat2940228&so=6"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Women's Eria Bijou Watch",
                    "price": "248.00",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://www.ashford.com/images/catalog/balmain/ashford/B17713282_FLA.jpg',
                    "country": 'us',
                    "description": "xxx"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.ashford.com/us/watches/calvin+klein/K3L33166.pid"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Women's Gentle Watch",
                    "price": "380.00",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://www.ashford.com/images/catalog/calvin-klein/gentle/K3L33166_FLA.jpg',
                    "country": 'us',
                    "description": "xxx"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Search page pagination', function() {

    //     var payload = {
    //         "pageType" : "pagination",
    //         "url" : ""
    //     }

    //     it('should be true when number of items and pages correct', function (done) {

    //         Structure(payload, function(pagination) {
    //             var expected = JSON.stringify({
    //                 "items": "",
    //                 "pages": ""
    //             })
    //             JSON.stringify(pagination).should.equal(expected);
    //             done()
    //         })
    //     });

    // })


    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.ashford.com/us/watches/Balmain/cat2940228.cid?N=14885&No=0&Nrpp=80&catId=cat2940228&pn=brand"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Balmain watches"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.ashford.com/us/watches/Balmain/cat2940228.cid?N=14885&No=0&Nrpp=80&catId=cat2940228&pn=brand"
        }

        // Change to be the number of products on the page
        var x = 234;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = "Women's Bellafina La...";
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '695',
                    url: 'http://www.ashford.com/us/watches/balmain/bellafina/bellafina-lady-round/B16753382.pid?nid=cpg_cat2940228&so=1&nid=cpg_cat2940228&so=1',
                    image: 'http://www.ashford.com/images/catalog/balmain/ashford/B16753382_FSA.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = "Men's Madrigal Chron...";
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '398',
                    url: 'http://www.ashford.com/us/watches/balmain/madrigal/madrigal-chrono-gent-xxl/B53413324.pid?nid=_cat2940228&so=73&nid=_cat2940228&so=73',
                    image: 'http://www.ashford.com/images/catalog/balmain/ashford/B53413324_FSA.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
