var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function () {

    this.timeout(10000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "http://www.babyearth.com/california-baby-overtired-cranky-shampoo-bodywash-8-5-oz.html",
            "html": cache.load("http://www.babyearth.com/california-baby-overtired-cranky-shampoo-bodywash-8-5-oz.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Overtired & Cranky Shampoo & Bodywash 8.5 Oz by California Baby",
                    "price": "12.95",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://s2.babyearth.com/images2/products/medium/11-5848-01.jpg',
                    "country": 'us',
                    "sku": 'california-baby-overtired-cranky-shampoo-bodywash-8-5-oz'
                    
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null,
    //                 "image": '',
    //                 "country": '',
    //                 "description": ''
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function () {

        var payload = {
            "pageType": "pagination",
            "url": "http://www.babyearth.com/bathing-products.html",
            "html": cache.load("http://www.babyearth.com/bathing-products.html")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function (err, pagination) {
                var expected = JSON.stringify({
                    "items": 12,
                    "pages": 10,
                    "url": "http://www.babyearth.com/bathing-products.html"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Non search page', function () {

        var payload = {
            "pageType": "pagination",
            "url": "http://www.babyearth.com/thule-chariot-chinook-1.html",
            "html": cache.load("http://www.babyearth.com/thule-chariot-chinook-1.html")
        }

        it('should be true when items and pages are null', function (done) {

            Structure(payload, function (err, pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null,
                    "url": "http://www.babyearth.com/thule-chariot-chinook-1.html"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "http://www.babyearth.com/bathing-products.html",
            "html": cache.load("http://www.babyearth.com/bathing-products.html")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Bathing Products",
                    "url": "http://www.babyearth.com/bathing-products.html"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.babyearth.com/collections/bathing-products",
            "html": cache.load("https://www.babyearth.com/collections/bathing-products")
        }

        // Change to be the number of products on the page
        var x = 18;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Under the Nile Organics Organic Hooded Kimono';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '38.00',
                    url: 'https://www.babyearth.com/products/under-the-nile-organics-organic-hooded-kimono',
                    image: 'https://cdn.shopify.com/s/files/1/2289/8945/products/10-5673-01_300x300.jpg?v=1517217287',
                    country: 'us',
                    sku: 'under-the-nile-organics-organic-hooded-kimono'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Hevea Baby Hevea Kawan Mini Teething & Bath Toy';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '17.00',
                    url: 'https://www.babyearth.com/products/hevea-baby-hevea-kawan-mini-teething-bath-toy',
                    image: 'https://cdn.shopify.com/s/files/1/2289/8945/products/12-3648-01_300x300.jpg?v=1517237749',
                    country: 'us',
                    sku:"hevea-baby-hevea-kawan-mini-teething-bath-toy"
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
