var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.babymonitorsdirect.co.uk', function() {
    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.babymonitorsdirect.co.uk/product/philips-avent-scd580-01-dect-baby-monitor-with-light-projector/",
            "html": cache.load("https://www.babymonitorsdirect.co.uk/product/philips-avent-scd580-01-dect-baby-monitor-with-light-projector/")
        }

        it('should be true when product is in stock', function () {

           const inStockPage =  Structure(payload)  
           inStockPage.name.should.be.equal("Philips Avent SCD580/01 DECT Baby Monitor with Light Projector");
           inStockPage.price.should.be.equal("155.00");
           inStockPage.url.should.be.equal("https://www.babymonitorsdirect.co.uk/product/philips-avent-scd580-01-dect-baby-monitor-with-light-projector/");
           inStockPage.inStock.should.be.equal(true);
           should(inStockPage.stockLevel).be.equal(null);
           inStockPage.inStock.should.be.equal(true);
           inStockPage.image.should.be.equal('https://cdn-babymonitorsdirect-co-uk.r.worldssl.net/wp-content/uploads/2018/07/Philips-Avent-SCD580-01-DECT-Baby-Monitor-with-Light-Projector.png');
           inStockPage.country.should.be.equal('uk');            
        });

    });


    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.bitsandpieces.com/category/childrens-puzzles",
            "html": cache.load("https://www.bitsandpieces.com/category/childrens-puzzles")
        }

        it('should be true when number of items and pages correct', function () {

            var expected = JSON.stringify({
                "items": 7,
                "pages": null,
                "url":"https://www.bitsandpieces.com/category/childrens-puzzles"
            })

            const pagination = Structure(payload)

            JSON.stringify(pagination).should.equal(expected);
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.bitsandpieces.com/category/childrens-puzzles",
            "html": cache.load("https://www.bitsandpieces.com/category/childrens-puzzles")
        }

        it('should be true when number of items and pages correct', function () {

            var expected = JSON.stringify({
                "name": "Children's Puzzles",
                "url":"https://www.bitsandpieces.com/category/childrens-puzzles"
            })

            const category = Structure(payload)

            JSON.stringify(category).should.equal(expected);
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.babymonitorsdirect.co.uk/products/video-monitoring/video-baby-monitors",
            "html": cache.load("http://www.babymonitorsdirect.co.uk/products/video-monitoring/video-baby-monitors")
        }

        // Change to be the number of products on the page
        var x = 15;
        it('should return ' + x + ' numer of products', function () {
            let result = Structure(payload)
            result.length.should.equal(x);
        });

        var firstProductName = 'Summer Infant Side By Side 2 Camera Video Baby Monitor';
        it('first product should be ' + firstProductName, function() {

            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '149.99',
                url: 'https://www.babymonitorsdirect.co.uk/product/summer-infant-side-by-side-2-camera-video-baby-monitor/',
                image: 'https://cdn-babymonitorsdirect-co-uk.r.worldssl.net/wp-content/uploads/2018/04/29626_Side-By-Side-Monitor_Silo4-247x247.png',
                country: 'uk'
            })
            
            let result = Structure(payload)

            var first = result.shift()
            
            JSON.stringify(first).should.equal(expected)

        })

        var lastProductName = 'Samsung Wi-Fi IP SmartCam SNH-P6410BNUK â€“ White';
        it('last product should be ' + lastProductName, function () {
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '109.99',
                url: 'https://www.babymonitorsdirect.co.uk/product/samsung-wi-fi-ip-smartcam-snh-p6410bnuk-white/',
                image: 'https://cdn-babymonitorsdirect-co-uk.r.worldssl.net/wp-content/uploads/2018/07/Samsung-Wi-Fi-IP-SmartCam-SNH-P6410BNUK-White-1-247x247.png',
                country: 'uk'
            })

            const result = Structure(payload);

            var last = result.pop()
            JSON.stringify(last).should.equal(expected)
        })

    });

});


