var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.babys-mart.co.uk', function() {


    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.babys-mart.co.uk/baby_gifts_and_toys/soft_toys/soft_animals/nattou-cappuccino-musical-toy-lamb"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Nattou Cappuccino Musical Toy Lamb",
                    "price": "21.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://www.babys-mart.co.uk/images/products/54f5acfed302e-musical-lamb_big_150423114237.jpg',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.babymonitorsdirect.co.uk/breathing-sensor-monitors.html?manufacturer=339"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 12,
                    "pages": 4
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.babys-mart.co.uk/baby_gifts_and_toys/developmental_toys/activity_toys"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "activity toys"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

        describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.babys-mart.co.uk/prams_pushchairs_and_accessories/buggy_and_pram_accessories/raincovers/quinny-yezz-raincover"
        }

        it('should return false when product is out of stock', function(done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Quinny Yezz Raincover",
                    "price": "17.99",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://www.babys-mart.co.uk/images/products/Quinny_Yezz_Raincover_2015_Blue_Blueloop_3qrt_big_150430162526.jpg',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

    })


    describe('Out of Stock Page 2', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.babys-mart.co.uk/nursery_furniture/furniture/nursery_wardrobes/Kidsmill_Bateau_Wardrobe_White"
        }

        it('should return false when product is out of stock', function(done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Kidsmill Bateau Wardrobe White",
                    "price": "895.00",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://www.babys-mart.co.uk/images/products/bateau-wardrobe_big_141014163435.jpg',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

    })

     describe('Search Page', function() {

            var payload = {
                "pageType" : "search",
                "url" : "https://www.babys-mart.co.uk/baby_gifts_and_toys/developmental_toys/activity_toys",
                "html": cache.load("https://www.babys-mart.co.uk/baby_gifts_and_toys/developmental_toys/activity_toys")

            }

            // Change to be the number of products on the page
            var x = 7;
            it('should return ' + x + ' numer of products', function () {
                let result = Structure(payload)
                result.length.should.equal(x);
            });
    
            var firstProductName = 'Critters Go For A Ride 100 Large Piece Jigsaw Puzzle';
            it('first product should be ' + firstProductName, function() {
    
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '4.99',
                    url: 'http://www.bitsandpieces.com/product/critters-go-for-a-ride-100-large-piece-jigsaw-puzzle-42777',
                    image: 'https://s3.amazonaws.com/cdn.bitsandpieces.com/images/160/42777.jpg',
                    country: 'us'
                })
                
                let result = Structure(payload)
    
                var first = result.shift()
                
                JSON.stringify(first).should.equal(expected)
    
            })
            var lastProductName = 'Soccer Sliding Wooden Puzzle';
            it('last product should be ' + lastProductName, function () {
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '14.99',
                    url: 'http://www.bitsandpieces.com/product/soccer-sliding-wooden-puzzle',
                    image: 'https://s3.amazonaws.com/cdn.bitsandpieces.com/images/160/43952.jpg',
                    country: 'us'
                })
    
                const result = Structure(payload);
    
                var last = result.pop()
                JSON.stringify(last).should.equal(expected)
            })
    
        });
    })
