var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.backcountry.com/hydro-photon-steripen-freedom?skid=HYD0023-METGN-ONESIZ&ti=UExQIENhdDpXYXRlciBQdXJpZmljYXRpb246MToxOmJjc0NhdDcxMTAwMDIyMg==",
            "html" : cache.load("http://www.backcountry.com/hydro-photon-steripen-freedom?skid=HYD0023-METGN-ONESIZ&ti=UExQIENhdDpXYXRlciBQdXJpZmljYXRpb246MToxOmJjc0NhdDcxMTAwMDIyMg==")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Hydro Photon SteriPEN Freedom",
                    "price": "95.96",
                    "url": payload.url,
                    "inStock": "UNCHECKED",
                    "stockLevel": null,
                    "image": "http://content.backcountry.com/images/items/small/HYD/HYD0023/METGN.jpg",
                    "country": "us",
                    "sku":"HYD0023-METGN-ONESIZ&ti=UExQIENhdDpXYXRlciBQdXJpZmljYXRpb246MToxOmJjc0NhdDcxMTAwMDIyMg="
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });


    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.backcountry.com/new-arrivals?fl=true",
            "html" : cache.load("http://www.backcountry.com/new-arrivals?fl=true")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 40,
                    "pages": 684,
                    "url":"http://www.backcountry.com/new-arrivals?fl=true"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.backcountry.com/water-purification",
            "html" : cache.load("http://www.toolbarn.com/milwaukee-48-11-2830.html")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "",
                    "url":"http://www.backcountry.com/water-purification"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.backcountry.com/skiing",
            "html" : cache.load("https://www.backcountry.com/skiing")
        }

        // Change to be the number of products on the page
        var x = 40;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = "Line Pescado Ski";
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '899.95',
                    url: 'https://www.backcountry.com/line-pescado-ski?skid=LIN002T-ONECOL-S180CM&ti=UExQIENhdDpTa2lpbmcgR2VhcjoxOjE6YmNzQ2F0NTEwMDAwNA==',
                    image: 'https://content.backcountry.com/images/items/medium/LIN/LIN002T/ONECOL.jpg',
                    country: 'us',
                    sku:"LIN002T-ONECOL-S180CM&ti=UExQIENhdDpTa2lpbmcgR2VhcjoxOjE6YmNzQ2F0NTEwMDAwNA="
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = "Salomon QST 99 Ski";
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '599.95',
                    url: 'https://www.backcountry.com/salomon-qst-99-ski?skid=SAL00TP-ONECOL-S167CM&ti=UExQIENhdDpTa2lpbmcgR2VhcjoxOjQwOmJjc0NhdDUxMDAwMDQ=',
                    image: 'https://content.backcountry.com/images/items/medium/SAL/SAL00TP/ONECOL.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
