var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function () {

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.banggood.com/FrSky-XM-2_4G-16CH-Telemetry-Receiver-p-1107435.html?rmmds=search&cur_warehouse=CN",
            "html": cache.load("https://www.banggood.com/FrSky-XM-2_4G-16CH-Telemetry-Receiver-p-1107435.html?rmmds=search&cur_warehouse=CN")
        }

        it('should be true whn product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "FrSky Ultralight XM Receiver Up To 16CH for RC Drone FPV Racing",
                    "price": "11.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://img.staticbg.com/thumb/view/oaupload/banggood/images/9E/1D/01854e06-901e-44f3-92fb-bc26e669a9c7.jpg',
                    "country": 'us',
                    "description": null,
                    "sku": "1107435"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.banggood.com/BlitzWolf-BW-F3-IPX5-Water-resistant-2x5W-Outdoor-Sport-Bluetooth-Speaker-p-1040559.html?rmmds=category&cur_warehouse=USA",
            "html": cache.load("https://www.banggood.com/BlitzWolf-BW-F3-IPX5-Water-resistant-2x5W-Outdoor-Sport-Bluetooth-Speaker-p-1040559.html?rmmds=category&cur_warehouse=USA")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "BlitzWolfÂ® BW-F3 IPX5 Water Resistant 2*5W Outdooors Sport Bluetooth Speaker",
                    "price": "26.99",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://img.staticbg.com/thumb/view/oaupload/banggood/images/36/23/d53b068e-7af2-4fd6-be0a-5ab2a01f2e37.jpg',
                    "country": 'us',
                    "description": null,
                    "sku":"1040559"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function () {

        var payload = {
            "pageType": "pagination",
            "url": "https://www.banggood.com/Wholesale-Speakers-c-2833.html",
            "html": cache.load("https://www.banggood.com/Wholesale-Speakers-c-2833.html")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function (err, pagination) {
                var expected = JSON.stringify({
                    "items": 44,
                    "pages": 8,
                    "url": payload.url
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.banggood.com/Wholesale-Speakers-c-2833.html",
            "html": cache.load("https://www.banggood.com/Wholesale-Speakers-c-2833.html")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Speakers",
                    "url": payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.banggood.com/Wholesale-Speakers-c-2833.html",
            "html": cache.load("https://www.banggood.com/Wholesale-Speakers-c-2833.html")
        }

        // Change to be the number of products on the page
        var x = 7;
        it('should return ' + x + ' numer of products', function () {
            let result = Structure(payload)
            result.length.should.equal(x);
        });

        var firstProductName = 'Critters Go For A Ride 100 Large Piece Jigsaw Puzzle';
        it('first product should be ' + firstProductName, function() {

            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '4.99',
                url: 'http://www.bitsandpieces.com/product/critters-go-for-a-ride-100-large-piece-jigsaw-puzzle-42777',
                image: 'https://s3.amazonaws.com/cdn.bitsandpieces.com/images/160/42777.jpg',
                country: 'us'
            })
            
            let result = Structure(payload)

            var first = result.shift()
            
            JSON.stringify(first).should.equal(expected)

        })

        var lastProductName = 'Soccer Sliding Wooden Puzzle';
        it('last product should be ' + lastProductName, function () {
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '14.99',
                url: 'http://www.bitsandpieces.com/product/soccer-sliding-wooden-puzzle',
                image: 'https://s3.amazonaws.com/cdn.bitsandpieces.com/images/160/43952.jpg',
                country: 'us'
            })

            const result = Structure(payload);

            var last = result.pop()
            JSON.stringify(last).should.equal(expected)
        })
    })

})
