var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function () {

    this.timeout(10000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.bargainmax.co.uk/transformers-rescue-bots-medix-the-doc-bot.html",
            "html": cache.load("https://www.bargainmax.co.uk/transformers-rescue-bots-medix-the-doc-bot.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Transformers Rescue Bots Medix The Doc-Bot",
                    "price": "12.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://www.bargainmax.co.uk/media/catalog/product/cache/1/image/500x360/9df78eab33525d08d6e5fb8d27136e95/D/A/DAF62BB0E77B42E32F74BF64C6ABA420.jpg',
                    "country": 'uk',
                    "sku": "transformers-rescue-bots-medix-the-doc-bot.html"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.bargainmax.co.uk/nav/type-of-toy/action-toys-and-playsets/action-figures",
            "html": cache.load("https://www.bargainmax.co.uk/nav/type-of-toy/action-toys-and-playsets/action-figures")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Action Figures",
                    "url": "https://www.bargainmax.co.uk/nav/type-of-toy/action-toys-and-playsets/action-figures",
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page pagination', function () {

        var payload = {
            "pageType": "pagination",
            "url": "https://www.bargainmax.co.uk/nav/type-of-toy/action-toys-and-playsets/action-figures",
            "html": cache.load("https://www.bargainmax.co.uk/nav/type-of-toy/action-toys-and-playsets/action-figures")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function (err, pagination) {
                var expected = JSON.stringify({
                    "items": 12,
                    "pages": 5,
                    "url": "https://www.bargainmax.co.uk/nav/type-of-toy/action-toys-and-playsets/action-figures"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.bargainmax.co.uk/superman-mega-punch.html",
            "html": cache.load("https://www.bargainmax.co.uk/superman-mega-punch.html")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Superman Mega Punch",
                    "price": "9.99",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://www.bargainmax.co.uk/media/catalog/product/cache/1/image/500x360/9df78eab33525d08d6e5fb8d27136e95/0/4/048D9329232E5B33DAB2A24118AEDA79.jpg",
                    "country": "uk",
                    "sku": "superman-mega-punch.html"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.bargainmax.co.uk/type-of-toy/baby-pre-school/baby-toys.html",
            "html": cache.load("https://www.bargainmax.co.uk/type-of-toy/baby-pre-school/baby-toys.html")
        }

        // Change to be the number of products on the page
        var x = 8;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Beat Bo Learnin lights Dance Mat';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '26.99',
                    url: 'https://www.bargainmax.co.uk/type-of-toy/baby-pre-school/baby-toys/beat-bo-learnin-lights-dance-mat.html',
                    image: 'https://www.bargainmax.co.uk/media/catalog/product/cache/1/small_image/153x/9df78eab33525d08d6e5fb8d27136e95/B/2/B2DE9832187CF1B2DF2CF281FCFE0324.jpg',
                    country: 'uk',
                    sku: "beat-bo-learnin-lights-dance-mat.html"

                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'My Little Pony Rainbow Dash Glow Pony';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '8.99',
                    url: 'https://www.bargainmax.co.uk/type-of-toy/baby-pre-school/baby-toys/my-little-pony-rainbow-dash-glow-pony.html',
                    image: 'https://www.bargainmax.co.uk/media/catalog/product/cache/1/small_image/153x/9df78eab33525d08d6e5fb8d27136e95/9/D/9DFA645671CFE8908DCF4C35C5385404.jpg',
                    country: 'uk',
                    sku: "my-little-pony-rainbow-dash-glow-pony.html"

                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
