var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.barneys.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.barneys.com/product/the-gigi-sandra-velvet-trimmed-checked-double-breasted-blazer-505925815.html",
            "html": cache.load("https://www.barneys.com/product/the-gigi-sandra-velvet-trimmed-checked-double-breasted-blazer-505925815.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Sandra Velvet-Trimmed Checked Double-Breasted Blazer",
                    "price": "760",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://product-images.barneys.com/is/image/Barneys/505925815_5_Detail?$pdp_flexH$",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.barneys.com/product/jeffrey-levinson-elina-clutch-505786370.html",
            "html": cache.load("https://www.barneys.com/product/jeffrey-levinson-elina-clutch-505786370.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Elina Clutch",
                    "price": "2750",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://product-images.barneys.com/is/image/Barneys/505786370_4_BagOpen?$pdp_flexH$",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.barneys.com/category/women/clothing/jackets/N-dfzzoo",
        "html": cache.load("https://www.barneys.com/category/women/clothing/jackets/N-dfzzoo")
    }

    // Change to be the number of products on the page
    var x = 96;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Classic Wool Plaid Twill One-Button Blazer';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '575',
                url: 'https://www.barneys.com/product/frame-classic-wool-plaid-twill-one-button-blazer-505812076.html',
                image: 'https://product-images.barneys.com/is/image/Barneys/505812076_1_TopFront?$oc_grid_fixed$',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Leroy Wool Double-Breasted Blazer';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '930',
                url: 'https://www.barneys.com/product/isabel-marant-leroy-wool-double-breasted-blazer-505660708.html',
                image: 'https://product-images.barneys.com/is/image/Barneys/505660708_1_TopFront?$oc_grid_fixed$',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.barneys.com/category/women/clothing/jackets/N-dfzzoo",
            "html": cache.load("https://www.barneys.com/category/women/clothing/jackets/N-dfzzoo")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Jackets",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
