describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.baseballsavings.com/baseball-apparel/softball-apparel-womens",
        "html": cache.load("https://www.baseballsavings.com/baseball-apparel/softball-apparel-womens")
    }

    // Change to be the number of products on the page
    var x = 24;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Mizuno Womens Select Belted Fastpitch Pants';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '35.95',
                url: 'https://www.baseballsavings.com/baseball-apparel/softball-apparel-womens/softball-pants-womens/mizuno-womens-select-belted-fast-pitch-pants-p27081',
                image: 'https://www.baseballsavings.com/wcsstore/CatalogAssetStore/Attachment/images/products/baseball/P27081/i-royal-qv.jpg',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Champro Adult Professional Athletic Sock';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '4.95',
                url: 'https://www.baseballsavings.com/champro-adult-professional-athletic-sock',
                image: 'https://www.baseballsavings.com/wcsstore/CatalogAssetStore/Attachment/images/products/baseball/P138302/t-royal-v.jpg',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.baseballsavings.com/baseball-apparel/softball-apparel-womens",
            "html": cache.load("https://www.baseballsavings.com/baseball-apparel/softball-apparel-womens")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Women's Softball Apparel",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
