    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.bathandbodyworks.com/family/index.jsp?categoryId=11827845&cm_sp=LN-_-Hand+Soaps+%26+Sanitizers-_-Soap+Holders&cp=12587004.12936131"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Soap Holders"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.bathandbodyworks.com/c/hand-soaps/all-hand-soaps"
        }

        // Change to be the number of products on the page
        var x = 48;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Sweet Cinnamon Pumpkin Gentle Foaming Hand Soap';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '6.50',
                    url: 'http://www.bathandbodyworks.com/p/sweet-cinnamon-pumpkin-gentle-foaming-hand-soap-023484152.html?cgid=all-hand-soaps',
                    image: 'http://www.bathandbodyworks.com/on/demandware.static/-/Sites-master-catalog/default/dwc9ee4321/crop/023484152_crop_b.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Bergamot & Wild Lavender Purely Clean Hand Soap';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '8.50',
                    url: 'http://www.bathandbodyworks.com/p/bergamot-and-wild-lavender-purely-clean-hand-soap-023443291.html?cgid=all-hand-soaps',
                    image: 'http://www.bathandbodyworks.com/on/demandware.static/-/Sites-master-catalog/default/dwbaeba8eb/crop/023443291_crop_b.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

