var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.bbtoystore.com/store/FKO_VB_AOU-hawkeye-4781.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Funko POP! Vinyl Bobble-Heads - Avengers Age of Ultron - HAWKEYE",
                    "price": "11.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://www.bbtoystore.com/mm5/funko/FKO_4781.jpg',
                    "country": 'us',
                    "description": 'Product DetailsVinyl Bobble-Head by Funko LLC.Each figure is brand new and comes in a display box.Item number: 4781 Approximate size: 4 inches Collect all of the Funko Avengers - Age of Ultron merchandise!'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.bbtoystore.com/store/FKO_VB_AOU-hulk-4776.html"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Funko POP! Vinyl Bobble-Heads - Avengers Age of Ultron - HULK",
                    "price": "11.99",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://www.bbtoystore.com/mm5/funko/FKO_4776.jpg',
                    "country": 'us',
                    "description": 'Product DetailsVinyl Bobble-Head by Funko LLC.Each figure is brand new and comes in a display box.Item number: 4776 Approximate size: 4 inches Collect all of the Funko Avengers - Age of Ultron merchandise!'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.bbtoystore.com/store/heroes-dc-universe-and-marvel-pop.html"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 48,
                    "pages": 5
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.bbtoystore.com/store/heroes-dc-universe-and-marvel-pop.html"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Funko Heroes, DC Universe & Marvel"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.bbtoystore.com/store/heroes-dc-universe-and-marvel-pop.html"
        }

        // Change to be the number of products on the page
        var x = 48;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Funko Dorbz Vinyl Figure - Marvel Series 1 - DEADPOOL';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '9.99',
                    url: 'http://www.bbtoystore.com/store/FKO_DZ_MV_S1_deadpool-5957.html',
                    image: 'http://www.bbtoystore.com/mm5/funko/FKO_5957_255x255.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Funko POP! Marvel - Daredevil TV Vinyl Figure - MATT MURDOCK (4 inch)';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '11.99',
                    url: 'http://www.bbtoystore.com/store/FKO_VB_MV_DDT_matt-murdock-7030.html',
                    image: 'http://www.bbtoystore.com/mm5/funko/FKO_7030_255x255.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});