var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.beauty.com/bareminerals-gen-nude-radiant-lipstick-honeybun/qxp582508?catid=12884"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Radiant Lipstick, Honeybun 0.12 oz (3.5 ml)",
                    "price": "20.00",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://s7d2.scene7.com/is/image/Drugstore/582508?wid=500&hei=500&op_sharpen=1',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.beauty.com/makeup/eyes/qxg12881-0"
        }

        // Change to be the number of products on the page
        var x = 18;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Urban Decay Eyeshadow Primer Potion, Original .33 oz';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '20.00',
                    url: 'http://www.beauty.com/urban-decay-eyeshadow-primer-potion-original/qxp348388?catid=66695',
                    image: 'http://s7d2.scene7.com/is/image/Drugstore/348388?wid=220&hei=220&op_sharpen=1',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'bareMinerals Liner Shadow Liner, Coffee Bean 1 ea';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '14.00',
                    url: 'http://www.beauty.com/bareminerals-liner-shadow-liner-coffee-bean/qxp219875?catid=12977',
                    image: 'http://s7d2.scene7.com/is/image/Drugstore/219875?wid=220&hei=220&op_sharpen=1',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
