var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.beautybrands.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.beautybrands.com/product/laura+geller+glamlash+dramatic+volumizing+mascara.do?refType=&from=fn",
            "html": cache.load("https://www.beautybrands.com/product/laura+geller+glamlash+dramatic+volumizing+mascara.do?refType=&from=fn")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Laura Geller GlamLASH Dramatic Volumizing Mascara",
                    "price": "24.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://ii.beautybrands.com/fcgi-bin/iipsrv.fcgi?FIF=/images/beautybrands/source/11010_default.tif&wid=570=&cvt=jpeg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.beautybrands.com/hair-bath-skincare-makeup/sexy+hair+style+sexy+hair+450+protect+heat+defense+hot+tool+spray-120994.do",
            "html": cache.load("https://www.beautybrands.com/hair-bath-skincare-makeup/sexy+hair+style+sexy+hair+450+protect+heat+defense+hot+tool+spray-120994.do")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Sexy Hair Style Sexy Hair 450 Protect Heat Defense Hot Tool Spray & Hair Shine Spray",
                    "price": null,
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://ii.beautybrands.com/fcgi-bin/iipsrv.fcgi?FIF=/images/beautybrands/source/10725_default.tif&qlt=75&wid=570&cvt=jpeg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.beautybrands.com/category/makeup/eye.do",
        "html": cache.load("https://www.beautybrands.com/category/makeup/eye.do")
    }

    // Change to be the number of products on the page
    var x = 30;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Peter Thomas Roth Lashes To Die For THE MASCARA';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '22.00',
                url: 'https://www.beautybrands.com/product/peter+thomas+roth+lashes+to+die+for+the+mascara.do?refType=&from=fn',
                image: 'https://ii.beautybrands.com/fcgi-bin/iipsrv.fcgi?FIF=/images/beautybrands/source/7090_default.tif&qlt=75&wid=275&cvt=jpeg',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Too Faced Shadow Insurance Glitter Glue';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '20.00',
                url: 'https://www.beautybrands.com/product/too+faced+shadow+insurance+glitter+glue.do?refType=&from=fn',
                image: 'https://ii.beautybrands.com/fcgi-bin/iipsrv.fcgi?FIF=/images/beautybrands/source/7296_default.tif&qlt=75&wid=275&cvt=jpeg',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.beautybrands.com/category/makeup/eye.do",
            "html": cache.load("https://www.beautybrands.com/category/makeup/eye.do")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Eye",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});


