var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.beautyencounter.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.beautyencounter.com/christian-dior-dior-addict-it-line-liquid-eyeliner-255385",
            "html": cache.load("https://www.beautyencounter.com/christian-dior-dior-addict-it-line-liquid-eyeliner-255385")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Christian Dior Dior Addict It-Line Liquid Eyeliner",
                    "price": "33.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.beautyencounter.com/pub/media/catalog/product/cache/image/620x620/e9c3970ab036de70892d86c6d221abfe/products/large/3348901204750.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.beautyencounter.com/invictus-by-paco-rabanne-for-men-250812",
            "html": cache.load("https://www.beautyencounter.com/invictus-by-paco-rabanne-for-men-250812")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Invictus by Paco Rabanne for Men",
                    "price": null,
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://www.beautyencounter.com/pub/media/catalog/product/cache/image/620x620/e9c3970ab036de70892d86c6d221abfe/products/large/3349668515677.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.beautyencounter.com/makeup/eye-makeup",
        "html": cache.load("https://www.beautyencounter.com/makeup/eye-makeup")
    }

    // Change to be the number of products on the page
    var x = 50;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'MAC Eye Kohl';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: null,
                url: 'https://www.beautyencounter.com/mac-eye-kohl-147792',
                image: 'https://www.beautyencounter.com/pub/media/catalog/product/cache/small_image/150x175/beff4985b56e3afdbeabfc89641a4582/products/large/773602010813.jpg',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Bobbi Brown Instant Pretty Eye & Cheek Palette';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '43.95',
                url: 'https://www.beautyencounter.com/bobbi-brown-instant-pretty-eye-cheek-palette-262132',
                image: 'https://www.beautyencounter.com/pub/media/catalog/product/cache/small_image/150x175/beff4985b56e3afdbeabfc89641a4582/products/large/716170138084.jpg',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.beautyencounter.com/makeup/eye-makeup",
            "html": cache.load("https://www.beautyencounter.com/makeup/eye-makeup")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Eye Makeup",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
