var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.beautyflash.co.uk/p44/dermalogica-age-smart-multivitamin-power-recovery-masque.html?deriv=75ml",
            "html" : cache.load("https://www.beautyflash.co.uk/p44/dermalogica-age-smart-multivitamin-power-recovery-masque.html?deriv=75ml")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Dermalogica AGE Smart Multivitamin Power Recovery Masque",
                    "price": "33.30",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://www.beautyflash.co.uk/images/products/44_ind_44_ind_power-recovery-masque-75ml.jpg',
                    "country": 'uk',
                    "description": "Tried multi masking yet? Our skin is not uniform and may have more than 1 concern that needs to be addressed with a face mask. Try Dermalogica's Power Rescue Masque Trio and start multi masking to help your skin. A powerful masque of concentrated antioxidant vitamins to help skin recover from damage while enhancing barrier properties and promoting healthy tissue regeneration.",
                    "sku":"p44"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.beautyflash.co.uk/p4271/glam-glow-power-mud.html?deriv=50g",
            "html" : cache.load("https://www.beautyflash.co.uk/p4271/glam-glow-power-mud.html?deriv=50g")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Glam Glow Power Mud",
                    "price": "49.99",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://www.beautyflash.co.uk/images/products/5802_ind_glamglow-powermud.jpg',
                    "country": 'uk',
                    "description": "*Winner of Sephora No.1 Editor Pick 2014 and Featured in Cosmopolitan 2015* Revolutionize your cleansing routine with Power Mud Mud to Oil Treatment. Indulge in a multi-sensory cleansing experience, uniting the Power of Mud & the Power of Oil to achieve super amplified, deep cleansing, yet gentle results. Designed For Men & Women of All Ages & All Skin Types. Glam Glow is only available to UK and ROI customers.",
                    "sku":"p4271"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Search page pagination', function() {

    //     var payload = {
    //         "pageType" : "pagination",
    //         "url" : ""
    //     }

    //     it('should be true when number of items and pages correct', function (done) {

    //         Structure(payload, function(pagination) {
    //             var expected = JSON.stringify({
    //                 "items": "",
    //                 "pages": ""
    //             })
    //             JSON.stringify(pagination).should.equal(expected);
    //             done()
    //         })
    //     });

    // })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.beautyflash.co.uk/dermalogica/age-smart.html",
            "html" : cache.load("https://www.beautyflash.co.uk/dermalogica/age-smart.html")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Age Smart",
                    "url":"https://www.beautyflash.co.uk/dermalogica/age-smart.html"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.beautyflash.co.uk/dermalogica/age-smart.html",
            "html" : cache.load("https://www.beautyflash.co.uk/dermalogica/age-smart.html")
        }

        // Change to be the number of products on the page
        var x = 16;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Skin Perfecting Set';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '57.60',
                    url: 'https://www.beautyflash.co.uk/p5273/dermalogica-skin-perfecting-set.html?deriv=Various',
                    image: 'https://www.beautyflash.co.uk/images/products/6840_ind_Dermalogica-Skin-Perfecting-Set.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'AGE Smart Overnight Retinol Repair';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '65.70',
                    url: 'https://www.beautyflash.co.uk/p4721/dermalogica-age-smart-overnight-retinol-repair.html?deriv=30ml',
                    image: 'https://www.beautyflash.co.uk/images/products/_ind_dermalogica-overnight-retinol-repair.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
