var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.beautylish.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.beautylish.com/s/wayne-goss-the-anniversary-set-volume-2",
            "html": cache.load("https://www.beautylish.com/s/wayne-goss-the-anniversary-set-volume-2")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "The Anniversary Set Volume 2",
                    "price": "241",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://dy6g3i6a1660s.cloudfront.net/1yGuxqsAwuqRiPmVchglZwR-BH4/zb_p.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.beautylish.com/s/wayne-goss-the-artist-brush",
            "html": cache.load("https://www.beautylish.com/s/wayne-goss-the-artist-brush")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "The Artist Brush",
                    "price": "150",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://dy6g3i6a1660s.cloudfront.net/UJjWIaAaDhFUC9yVuUh5LQR-BH4/zb_p.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.beautylish.com/shop/browse?category=makeup-brushes",
        "html": cache.load("https://www.beautylish.com/shop/browse?category=makeup-brushes")
    }

    // Change to be the number of products on the page
    var x = 36;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'The Eye Set';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '130',
                url: 'https://www.beautylish.com/p/wayne-goss-the-eye-set',
                image: 'https://dy6g3i6a1660s.cloudfront.net/wi-YteTvNTaxSt1wdSfHKQR-BH4/320_p-ff/wayne-goss-the-eye-set.jpg',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Brush 07 Precision Lip Brush';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '17',
                url: 'https://www.beautylish.com/p/wayne-goss-brush-07-precision-lip-brush',
                image: 'https://dy6g3i6a1660s.cloudfront.net/r7mqKRTpP5zF-QQiLvdAFgR-BH4/320_p-28/wayne-goss-brush-07-precision-lip-brush.jpg',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.beautylish.com/shop/browse?category=makeup-brushes",
            "html": cache.load("https://www.beautylish.com/shop/browse?category=makeup-brushes")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Makeup Brushes",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
