var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.bedbathandbeyond.com', function() {

    before(function()  {
        this.timeout(5000);
    })

     describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.bedbathandbeyond.com/store/category/college/furniture/10623/?fromCollege=true&_requestid=276319"
        }

        it('should return 96 products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(96);
                done()
            })
        });

        it('last product should be Futon', function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'Tractor Seat Task Chair',
                    price: '46.99',
                    url: 'http://www.bedbathandbeyond.com/store/product/tractor-seat-task-chair/3282414?categoryId=10623',
                    image: 'https://s7d1.scene7.com/is/image/BedBathandBeyond/7432163282414m?$229$',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

        it('first product should be a BABY CARE™ Large Baby Play Mat in Birds in Trees', function(done) {

            Structure(payload, function(result) {

                 var first = result.shift()
                    var expected = JSON.stringify({
                        barcode: null,
                        name: 'Folding Moon Chair in Khaki',
                        price: '79.99',
                        url: 'http://www.bedbathandbeyond.com/store/product/folding-moon-chair-in-khaki/1045907579?categoryId=10623',
                        image: 'https://s7d2.scene7.com/is/image/BedBathandBeyond/83087145907579p?$229$',
                        country: 'us'
                    })

                    JSON.stringify(first).should.equal(expected)
                    done()
                })

        })


    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.bedbathandbeyond.com/store/category/baby-kids/infant-toys-ages-0-3-years/10572/?_requestid=4161317"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 96,
                    "pages": 23
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.bedbathandbeyond.com/store/category/baby-kids/infant-toys-ages-0-3-years/10572/?_requestid=4161317"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Infant Toys (Ages 0 - 3 Years)"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.bedbathandbeyond.com/store/product/lasting-color-cotton-bath-towel-collection-by-westpoint-home-trade/202147?categoryId=13433"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Lasting Color Cotton Bath Towel Collection by WestPoint Home™",
                    "price": "5.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://s7d9.scene7.com/is/image/BedBathandBeyond/208081202147c?$478$',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });


    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.bedbathandbeyond.com//store/product/geek-out-game/1045922268?categoryId=12984#"
        }

        it('should return false when product is out of stock', function(done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Geek Out! Game",
                    "price": "19.99",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://s7d9.scene7.com/is/image/BedBathandBeyond/77151945922268p?$478$',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

    })


 });







