    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.belk.com/for-the-home/?pmpt=all&pmin=0&pmid=BelkClearancePricing",
            "html" : cache.load("https://www.belk.com/for-the-home/?pmpt=all&pmin=0&pmid=BelkClearancePricing")

            
        }

        // Change to be the number of products on the page
        var x = 84;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'BiltmoreÂ® Hotel Solid Bath Towel Collection';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '15.00',
                    url: 'https://www.belk.com/p/biltmore-hotel-solid-bath-towel-collection/92004931173185.html?dwvar_92004931173185_color=360',
                    image: 'https://belk.scene7.com/is/image/Belk?layer=0&src=9200493_7460079_A_480_T30L00&layer=1&src=9200493_7460079_A_480_T30L01&layer=2&src=9200493_7460079_A_480_T30L02&op_colorize=167,173,152,norm,0&op_brightness=0&op_contrast=0&op_saturation=0&layer=comp&$DWP_PRODUCT_PLP_DESKTOP_t1$',
                    country: 'us',
                    sku:"92004931173185"
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Occasionally Made Ceramic Mug- Ya\'ll Spoken Here';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '18.00',
                    url: 'https://www.belk.com/p/occasionally-made-ceramic-mug--yall-spoken-here/7601607HSWMUGYALB.html?dwvar_7601607HSWMUGYALB_color=100312357586',
                    image: 'https://belk.scene7.com/is/image/Belk?layer=0&src=7601607_HSWMUGYALB_A_100_T10L00&layer=comp&$DWP_PRODUCT_PLP_DESKTOP_t1$',
                    country: 'us',
                    sku:"7601607HSWMUGYALB"
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

