var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.bellacor.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.bellacor.com/productdetail/artcraft-ac8290bk-fremont-black-one-light-7-inch-wide-outdoor-wall-sconce-with-clear-four-sid-1561237.htm",
            "html": cache.load("https://www.bellacor.com/productdetail/artcraft-ac8290bk-fremont-black-one-light-7-inch-wide-outdoor-wall-sconce-with-clear-four-sid-1561237.htm")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Artcraft Fremont Black One-Light 7-Inch Wide Outdoor Wall Sconce with Clear Four Side Glass",
                    "price": "182.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://mediacdn.bellacor.com/images/500/237AC8290BK.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.bellacor.com/productdetail/robert-abbey-s248-rico-espinet-scout-polished-nickel-one-light-sconce-1741077.htm",
            "html": cache.load("https://www.bellacor.com/productdetail/robert-abbey-s248-rico-espinet-scout-polished-nickel-one-light-sconce-1741077.htm")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Robert Abbey Rico Espinet Scout Polished Nickel One-Light Sconce",
                    "price": "335.34",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://mediacdn.bellacor.com/images/500/2238S248.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.bellacor.com/outdoor-wall-lighting.htm",
        "html": cache.load("https://www.bellacor.com/outdoor-wall-lighting.htm")
    }

    // Change to be the number of products on the page
    var x = 24;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Sebring Small Outdoor Wall-Mounted Lantern';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '89.00',
                url: 'https://www.bellacor.com/productdetail/sea-gull-lighting-8870-44-sebring-small-outdoor-wall-mounted-lantern-56706.htm',
                image: 'https://mediacdn.bellacor.com/images/500/743887044-055.jpg',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Plantation Outdoor Wall-Mounted Lantern';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '329.00',
                url: 'https://www.bellacor.com/productdetail/hinkley-2226cb-plantation-outdoor-wall-mounted-lantern-52751.htm',
                image: 'https://mediacdn.bellacor.com/images/500/4902226CB-055.jpg',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.bellacor.com/outdoor-wall-lighting.htm",
            "html": cache.load("https://www.bellacor.com/outdoor-wall-lighting.htm")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Outdoor Wall Lighting",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
