var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')


describe('www.benqdirect.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.benqdirect.com/monitors/22-monitors/benq-rl2240he-gaming-monitor.html",
            "html" : cache.load("http://www.benqdirect.com/monitors/22-monitors/benq-rl2240he-gaming-monitor.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "BenQ RL2240HE 21.5\" 1ms GTG Console Gaming Monitor",
                    "price": "149.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://www.benqdirect.com/media/catalog/product/cache/1/thumbnail/56x/9df78eab33525d08d6e5fb8d27136e95/r/l/rl2240he-image11.jpg',
                    "country": 'us',
                    "sku":"benq-rl2240he-gaming-monitor"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.benqdirect.com/monitors/22-monitors/vw2230h-1994.html",
            "html" : cache.load("http://www.benqdirect.com/monitors/22-monitors/vw2230h-1994.html")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "BenQ VW2230H 21.5\" 1080P w/ HDMI Monitor (White) - Refurbished",
                    "price": "91.24",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://www.benqdirect.com/media/catalog/product/cache/1/thumbnail/56x/9df78eab33525d08d6e5fb8d27136e95/v/w/vw2230_front_p_3.png',
                    "country": 'us',
                    "sku":"vw2230h-1994"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.benqdirect.com/accessories/benq-gear.html",
            "html" : cache.load("https://www.benqdirect.com/accessories/benq-gear.html")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 15,
                    "pages": 2,
                    "url" : "https://www.benqdirect.com/accessories/benq-gear.html"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.benqdirect.com/projectors/home-theater.html",
            "html" : cache.load("https://www.benqdirect.com/projectors/home-theater.html")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Home Theater Projectors",
                    "url" : "https://www.benqdirect.com/projectors/home-theater.html"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.benqdirect.com/projectors/home-theater.html",
            "html" : cache.load("https://www.benqdirect.com/projectors/home-theater.html")
        }

        // Change to be the number of products on the page
        var x = 9;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'BenQ HT2550 True 4k UHD HDR Home Theater Projector';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '1499.00',
                    url: 'https://www.benqdirect.com/projectors/home-theater/ht2550.html',
                    image: 'https://www.benqdirect.com/media/catalog/product/cache/1/small_image/400x/9df78eab33525d08d6e5fb8d27136e95/1/5/1509529590.png',
                    country: 'us',
                    sku:'ht2550'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Soft Carrying Case for HT2050 and HT3050';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '49.00',
                    url: 'https://www.benqdirect.com/projectors/home-theater/carry-case-ht2050-ht3050.html',
                    image: 'https://www.benqdirect.com/media/catalog/product/cache/1/small_image/400x/9df78eab33525d08d6e5fb8d27136e95/5/j/5j.j2v09.001.jpg',
                    country: 'us',
                    sku:"carry-case-ht2050-ht3050"

                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
