var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.bettymills.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.bettymills.com/metered-air-cabinet-1-dispenser-each-claire-cl7-madisp-c?referer=search",
            "html": cache.load("https://www.bettymills.com/metered-air-cabinet-1-dispenser-each-claire-cl7-madisp-c?referer=search")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Claire Metered Air Cabinet - 1 Dispenser Each",
                    "price": "20.40",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://cf1.bettymills.com/store/images/product/CLACL7-MADISP-C.JPG",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.bettymills.com/steel-strapping-strapbinder-705-1007005",
            "html": cache.load("https://www.bettymills.com/steel-strapping-strapbinder-705-1007005")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Strapbinder Steel Strapping",
                    "price": "220.00",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://cf1.bettymills.com/store/images/product/705-1003655.eps.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.bettymills.com/shop/product/find/Air+Freshener+%26+Odor",
        "html": cache.load("https://www.bettymills.com/shop/product/find/Air+Freshener+%26+Odor")
    }

    // Change to be the number of products on the page
    var x = 48;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'LYSOL® Neutra Air® Sanitizing Spray';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '3.89',
                url: 'https://www.bettymills.com/lysol-neutra-air-sanitizing-spray-79196?referer=search',
                image: 'https://cf1.bettymills.com/store/images/product/small/RAC79196EA.JPG',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Claire - Warm Welcome Metered Air Freshener';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '40.59',
                url: 'https://www.bettymills.com/warm-welcome-metered-air-freshener-claire-cl146?referer=search',
                image: 'https://cf1.bettymills.com/store/images/product/small/C146_WARM%20WELCOME.JPG',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.bettymills.com/shop/product/find/Air+Freshener+%26+Odor",
            "html": cache.load("https://www.bettymills.com/shop/product/find/Air+Freshener+%26+Odor")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Air Freshener & Odor",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
