var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.bhcosmetics.com/eyes/gold-rush-eye-cheek-palette"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Gold Rush Eye & Cheek Palette",
                    "price": "9.00",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://www.bhcosmetics.com/media/catalog/product/cache/1/image/450x450/9df78eab33525d08d6e5fb8d27136e95/h/o/holiday_palette_gold_rush_hero__three_quarter_open.jpg',
                    "country": 'us',
                    "description": 'Go for the gold and create day-to-night glam with our bold Gold Rush Eye & Cheek palette. Use the 10 warm-toned matte, satin, and shimmer shadows --from deep bronze and eggplant to pure white and innocent ivory-- to achieve multiple festive eye looks, and the coordinating bronzer, blush and highlighter to accent features and give skin a healthy glow. You will be gala gorgeous with the flick of a brush!'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.bhcosmetics.com/eyes/88-color-shimmer-eyeshadow-palette?___SID=U"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "88 Shimmer - Eighty-Eight Color Eyeshadow Palette",
                    "price": null,
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://www.bhcosmetics.com/media/catalog/product/cache/1/image/450x450/9df78eab33525d08d6e5fb8d27136e95/8/8/88shimmer_eyeshadow_palette_above960x960.png',
                    "country": 'us',
                    "description": 'Glisten up! Show your true colors with the icy options in our 88 Shimmer palette, perfect for putting a twinkle in your eye with high-voltage pops of color. Illuminate lids, highlight beneath the brow, apply a lighter shade to the inner corners of eyes, or mix brights with the natural and smoky shades for dazzling looks that are yours alone.'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    /*describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : ""
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": "",
                    "pages": ""
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })*/

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.bhcosmetics.com/eyes"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Eyes"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.bhcosmetics.com/new-arrivals",
            "html" : cache.load("https://www.bhcosmetics.com/new-arrivals")
        }

        // Change to be the number of products on the page
        var x = 9;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Gold Rush Eye & Cheek Palette';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '9.00',
                    url: 'http://www.bhcosmetics.com/eyes/gold-rush-eye-cheek-palette',
                    image: 'http://www.bhcosmetics.com/media/catalog/product/cache/1/small_image/300x/85e4522595efc69f496374d01ef2bf13/h/o/holiday_palette_gold_rush_hero__three_quarter_open.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = '88 Shimmer - Eighty-Eight Color Eyeshadow Palette';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: null,
                    url: 'http://www.bhcosmetics.com/eyes/88-color-shimmer-eyeshadow-palette?___SID=U',
                    image: 'http://www.bhcosmetics.com/media/catalog/product/cache/1/small_image/300x/85e4522595efc69f496374d01ef2bf13/8/8/88shimmer_eyeshadow_palette_openabove960x960.png',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
