var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.big5sportinggoods.com', function () {

    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.big5sportinggoods.com/store/details/Columbia+Womens-Easy-Going-Cowl-Neck-Long-Sleeve-Shirt/4325112952041/_/A-6186977#.W5cbNJMzY1g",
            "html": cache.load("https://www.big5sportinggoods.com/store/details/Columbia+Womens-Easy-Going-Cowl-Neck-Long-Sleeve-Shirt/4325112952041/_/A-6186977#.W5cbNJMzY1g")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Columbia Women's Easy Going Cowl Neck Long-Sleeve Shirt",
                    "price": "39.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.big5sportinggoods.com/catalogimage/img/product/large/4325_11295_2041_400_large_02.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.big5sportinggoods.com/store/details//_/A-5952171#.W5cbaJMzY1g",
            "html": cache.load("https://www.big5sportinggoods.com/store/details//_/A-5952171#.W5cbaJMzY1g")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "adidas Women's D2M Cuff Pants",
                    "price": "29.99",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://www.big5sportinggoods.com/catalogimage/img/product/large/4356_11162_4638_016_large_02.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });


    describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "http://big5sportinggoods.com/store/browse/sports/_/N-84a",
        "html": cache.load("http://big5sportinggoods.com/store/browse/sports/_/N-84a")
    }

    // Change to be the number of products on the page
    var x = 7;
    it('should return ' + x + ' numer of products', function () {
        let result = Structure(payload)
        result.length.should.equal(x);
    });

    var firstProductName = 'Critters Go For A Ride 100 Large Piece Jigsaw Puzzle';
    it('first product should be ' + firstProductName, function() {

        var expected = JSON.stringify({
            barcode: null,
            name: firstProductName,
            price: '4.99',
            url: 'http://www.bitsandpieces.com/product/critters-go-for-a-ride-100-large-piece-jigsaw-puzzle-42777',
            image: 'https://s3.amazonaws.com/cdn.bitsandpieces.com/images/160/42777.jpg',
            country: 'us'
        })
        
        let result = Structure(payload)

        var first = result.shift()
        
        JSON.stringify(first).should.equal(expected)

    })

    var lastProductName = 'Soccer Sliding Wooden Puzzle';
    it('last product should be ' + lastProductName, function () {
        var expected = JSON.stringify({
            barcode: null,
            name: lastProductName,
            price: '14.99',
            url: 'http://www.bitsandpieces.com/product/soccer-sliding-wooden-puzzle',
            image: 'https://s3.amazonaws.com/cdn.bitsandpieces.com/images/160/43952.jpg',
            country: 'us'
        })

        const result = Structure(payload);

        var last = result.pop()
        JSON.stringify(last).should.equal(expected)
    })


    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.big5sportinggoods.com/store/browse/apparel/women-s-apparel/shirtstops/_/N-adl?nf=t",
            "html": cache.load("https://www.big5sportinggoods.com/store/browse/apparel/women-s-apparel/shirtstops/_/N-adl?nf=t")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Shirts/Tops",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
