var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.bigbadtoystore.com/bbts/product.aspx?product=ORI10018&mode=retail",
            "html": cache.load("http://www.bigbadtoystore.com/bbts/product.aspx?product=ORI10018&mode=retail")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Hero of Steel 01 - Optimus Prime With Bonus Megatron Pistol",
                    "price": "159.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://www.bigbadtoystore.com/images/products/in/large/ORI10018.jpg',
                    "country": 'us',
                    "description": 'Product Size: 9" (23cm)Material: ABS & Die-CastHero of Steel 01 - Optimus Prime is a stylized non transforming figure with over 40 points of articulation.'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.bigbadtoystore.com/bbts/product.aspx?product=BAN17045&mode=retail",
            "html": cache.load("http://www.bigbadtoystore.com/bbts/product.aspx?product=BAN17045&mode=retail")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Toy Story Chogokin Combination Woody Robo Sheriff Star",
                    "price": "169.99",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://www.bigbadtoystore.com/images/products/in/large/BAN17045.jpg',
                    "country": 'us',
                    "description": 'Product size: Approx 9.1" Package size: Approx 11.0"x14.6"x4.7"Set contains 5 characters: WoodyBullseyeSlinky DogHammRexWoody and friends can combine!? A totally wild, only in Japan re-envisioning of your favorite characters from the hit Disney/Pixar Toy Story series! Woody, Rex, Bullseye, Slinky Dog, and Hamm transform and combine to form the giant 9 inch tall Chogokin WOODY ROBO SHERIFF STAR! 									   This item is in Standard Grade Condition:'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.bigbadtoystore.com/bbts/search.aspx?search=toy",
            "html": cache.load("http://www.bigbadtoystore.com/bbts/search.aspx?search=toy")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 20,
                    "pages": 10,
                    "url":"http://www.bigbadtoystore.com/bbts/search.aspx?search=toy"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.bigbadtoystore.com/bbts/search.aspx?search=toy",
            "html": cache.load("http://www.bigbadtoystore.com/bbts/search.aspx?search=toy")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Toys",
                    "url" : "http://www.bigbadtoystore.com/bbts/search.aspx?search=toy"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });
    
    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.bigbadtoystore.com/search?Brand=614",
            "html": cache.load("http://www.bigbadtoystore.com/search?Brand=614")
        }

        // Change to be the number of products on the page
        var x = 20;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Hero of Steel 01 - Optimus Prime With Bonus Megatron Pistol';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '159.99',
                    url: 'http://www.bigbadtoystore.com/bbts/product.aspx?product=ORI10018&mode=retail',
                    image: 'http://www.bigbadtoystore.com/images/products/in/small/ORI10018.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Gravity Builder: GT-01C Navvy';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '99.99',
                    url: 'http://www.bigbadtoystore.com/bbts/product.aspx?product=GTY10002&mode=retail',
                    image: 'http://www.bigbadtoystore.com/images/products/in/small/GTY10002.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
