describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.bikenashbar.com/cycling/bikes-frames/road-bikes#facet:&productBeginIndex:0&facetLimit:&orderBy:5&pageView:grid&minPrice:&maxPrice:&pageSize:&",
        "html": cache.load("https://www.bikenashbar.com/cycling/bikes-frames/road-bikes#facet:&productBeginIndex:0&facetLimit:&orderBy:5&pageView:grid&minPrice:&maxPrice:&pageSize:&")
    }

    // Change to be the number of products on the page
    var x = 24;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Cavalo 105 Alloy Road Bike';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '799.99',
                url: 'https://www.bikenashbar.com/cycling/bikes-frames/road-bikes/cavalo-105-alloy-road-bike-cv-arb',
                image: 'https://media.bikenashbar.com/images/nashbar/products/product-hi/CV-ARB-NCL-SIDE.jpg?resize=200px:200px&output-quality=75',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = "Raleigh Amelia 3 Women's Gravel Bike - Closeout";
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '1049.99',
                url: 'https://www.bikenashbar.com/cycling/bikes-frames/road-bikes/raleigh-amelia-3-yb-am3',
                image: 'https://media.bikenashbar.com/images/nashbar/products/product-hi/YB-AM3-NCL-ANGLE.jpg?resize=200px:200px&output-quality=75',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.bikenashbar.com/cycling/bikes-frames/road-bikes#facet:&productBeginIndex:0&facetLimit:&orderBy:5&pageView:grid&minPrice:&maxPrice:&pageSize:&",
            "html": cache.load("https://www.bikenashbar.com/cycling/bikes-frames/road-bikes#facet:&productBeginIndex:0&facetLimit:&orderBy:5&pageView:grid&minPrice:&maxPrice:&pageSize:&")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Road Bikes",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
