var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.bjs.com/call-of-duty--wwii--ps4-.product.3000000000000989104",
            "html": cache.load("http://www.bjs.com/call-of-duty--wwii--ps4-.product.3000000000000989104")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Call of Duty: WWII (PS4)",
                    "price": "53.99",
                    "url": 'http://www.bjs.com/call-of-duty--wwii--ps4-.product.3000000000000989104',
                    "inStock": "UNCHECKED",
                    "stockLevel": null,
                    "image": 'http://richmedia.channeladvisor.com/ImageDelivery/imageService?profileId=52000717&id=1154696&recipeId=244',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.bjs.com/search?Ntt=toys&N=",
            "html": cache.load("http://www.bjs.com/search?Ntt=toys&N=")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 120,
                    "pages": 4,
                    "url": payload.url
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.bjs.com/xbox/xbox-one-video-games.category.3000000000000131473.3000000000000063530.2002375.1?N=2002375",
            "html": cache.load("http://www.bjs.com/xbox/xbox-one-video-games.category.3000000000000131473.3000000000000063530.2002375.1?N=2002375")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Xbox One Video Games",
                    "url": payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });


    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.bjs.com/category/toys-and-video-games/indoor-play/play-kitchens/835",
            "html": cache.load("https://www.bjs.com/category/toys-and-video-games/indoor-play/play-kitchens/835")
        }

        // Change to be the number of products on the page
        var x = 9;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Real Cooking Ultimate Baking Value Pack Starter Set';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '16.98',
                    url: 'https://www.bjs.com/product/real-cooking-ultimate-baking-value-pack-starter-set/288055',
                    image: 'https://richmedia.channeladvisor.com/ImageDelivery/imageService?profileId=52000717&imageID=171321941&recipeName=180x180',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Step2 Contemporary Chef Kitchen';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '99.99',
                    url: 'https://www.bjs.com/product/step2-contemporary-chef-kitchen/3000000000001044176',
                    image: 'https://richmedia.channeladvisor.com/ImageDelivery/imageService?profileId=52000717&imageID=121180&recipeName=180x180',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
