var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.bonton.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.bonton.com/sc1/bed-bath/bath-towels/luxury-spa-collection-650-gsm-6-pc-towel-set-904177.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Luxury Spa Collection 650 GSM 6-pc. Towel Set",
                    "price": "52.80",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://s7d4.scene7.com/is/image/BonTon/904177_gray?$ibm_large$',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });
   
    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.bonton.com/sc1/bed-bath/bath-towels/?pageSize=#ctx=closedFacets%3Aicon_facet_2_5_3074457345618260605_3074457345618265106%3Bicon_facet_3_5_3074457345618260605_3074457345618265106%3Bicon_facet_4_5_3074457345618260605_3074457345618265106%3Bicon_facet_5_5_3074457345618260605_3074457345618265106%3Bicon_facet_6_5_3074457345618260605_3074457345618265106%3Bicon_facet_7_5_3074457345618260605_3074457345618265106%3Bicon_facet_9_5_3074457345618260605_3074457345618265106%3B&pos=0"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 360,
                    "pages": 1
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.bonton.com/sc1/bed-bath/bath-towels/?pageSize=#ctx=closedFacets%3Aicon_facet_2_5_3074457345618260605_3074457345618265106%3Bicon_facet_3_5_3074457345618260605_3074457345618265106%3Bicon_facet_4_5_3074457345618260605_3074457345618265106%3Bicon_facet_5_5_3074457345618260605_3074457345618265106%3Bicon_facet_6_5_3074457345618260605_3074457345618265106%3Bicon_facet_7_5_3074457345618260605_3074457345618265106%3Bicon_facet_9_5_3074457345618260605_3074457345618265106%3B&pos=0"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Bath Towels"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.bonton.com/sc1/bed-bath/bath-towels/?pageSize="
        }

        // Change to be the number of products on the page
        var x = 36;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Luxury Spa Collection 650 GSM 6-pc. Towel Set';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '88.00',
                    url: 'http://www.bonbon.com/sc1/bed-bath/bath-towels/luxury-spa-collection-650-gsm-6-pc-towel-set-904177.html',
                    image: 'http://s7d4.scene7.com/is/image/BonTon/904177?$medium_245$',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Villa di Borghese Ravello 3-pc. Towel Set';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '90.00',
                    url: 'http://www.bonbon.com/sc1/bed-bath/bath-towels/villa-di-borghese-ravello-3-pc-towel-set-907179.html',
                    image: 'http://s7d4.scene7.com/is/image/BonTon/907179?$medium_245$',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
