var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {
    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.brandsmartusa.com/sky+devices/218743/platinum+6+0+plus+unlocked+android+smartphone.htm",
            "html" : cache.load("https://www.brandsmartusa.com/sky+devices/218743/platinum+6+0+plus+unlocked+android+smartphone.htm")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Eureka - Lightweight Stick Vacuum",
                    "price": "26.88",
                    "url": payload.url,
                    "inStock": "UNCHECKED",
                    "stockLevel": null,
                    "image": "http://www.brandsmartusa.com/images/product/large/20149162.jpg?isrc=1111",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null,
    //                 "image": "",
    //                 "country": "us"
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.brandsmartusa.com/housewares/floor+care/vacuums/clearance+items/_/N-103059",
            "html" : cache.load("http://www.brandsmartusa.com/housewares/floor+care/vacuums/clearance+items/_/N-103059")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 80,
                    "pages": 3,
                    "url": "http://www.brandsmartusa.com/housewares/floor+care/vacuums/clearance+items/_/N-103059"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });


    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.brandsmartusa.com/housewares/floor+care/vacuums/clearance+items/_/N-103059",
            "html" : cache.load("http://www.brandsmartusa.com/housewares/floor+care/vacuums/clearance+items/_/N-103059")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Clearance Items",
                    "url":"http://www.brandsmartusa.com/housewares/floor+care/vacuums/clearance+items/_/N-103059"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.brandsmartusa.com/cell+phones/cell+phone+accessories/bluetooth+headsets/_/N-4214704376",
            "html" : cache.load("https://www.brandsmartusa.com/cell+phones/cell+phone+accessories/bluetooth+headsets/_/N-4214704376")
        }

        // Change to be the number of products on the page
        var x = 108;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Canon - 20.0 MP PowerShot IS Wi-Fi Digital Camera';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '249.88',
                    url: 'http://www.brandsmartusa.com/canon/198822/20+0+mp+powershot+is+wi+fi+digital+camera.htm',
                    image: 'http://www.brandsmartusa.com/images/product/small/20203846.jpg?isrc=1111',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Canon - 16.0 MP Powershot Digital Camera Kit';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '459.88',
                    url: 'http://www.brandsmartusa.com/canon/213120/16+0+mp+powershot+digital+camera+kit.htm',
                    image: 'http://www.brandsmartusa.com/images/product/small/20226439.jpg?isrc=1111',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
